INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (1, 398481, 1, N'A ', N'AGRICULTURE, FORESTRY AND FISHING ', N'A ', N'This section includes the exploitation of vegetal and animal natural resources, comprising the activities of growing of crops, raising and breeding of animals, harvesting of timber and other plants, animals or animal products from a farm or their natural habitats. ', N'', N'', N'', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (2, 398482, 2, N'01 ', N'Crop and animal production, hunting and related service activities ', N'01 ', N'This division includes two basic activities, namely the production of crop products and production of animal products, covering also the forms of organic agriculture, the growing of genetically modified crops and the raising of genetically modified animals. This division includes growing of crops in open fields as well in greenhouses. Group 01.5 (Mixed farming) breaks with the usual principles for identifying main activity. It accepts that many agricultural holdings have reasonably balanced crop and animal production, and that it would be arbitrary to classify them in one category or the other. ', N'This division also includes service activities incidental to agriculture, as well as hunting, trapping and related activities. ', N'', N'Agricultural activities exclude any subsequent processing of the agricultural products (classified under divisions 10 and 11 (Manufacture of food products and beverages) and division 12 (Manufacture of tobacco products)), beyond that needed to prepare them for the primary markets. The preparation of products for the primary markets is included here.The division excludes field construction (e.g. agricultural land terracing, drainage, preparing rice paddies etc.) classified in section F (Construction) and buyers and cooperative associations engaged in the marketing of farm products classified in section G. Also excluded is the landscape care and maintenance, which is classified in class 81.30. ', N'A ', 1)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (3, 398483, 3, N'01.1 ', N'Growing of non-perennial crops ', N'011 ', N'This group includes the growing of non-perennial crops, i.e. plants that do not last for more than two growing seasons. Included is the growing of these plants for the purpose of seed production. ', N'', N'', N'', N'01 ', 2)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (4, 398484, 4, N'01.11 ', N'Growing of cereals (except rice), leguminous crops and oil seeds ', N'0111 ', N'This class includes all forms of growing of cereals, leguminous crops and oil seeds in open fields. The growing of these crops is often combined within agricultural units.This class includes:- growing of cereals such as:  • wheat  • grain maize  • sorghum  • barley  • rye  • oats  • millets  • other cereals n.e.c.- growing of leguminous crops such as:  • beans  • broad beans  • chick peas  • cow peas  • lentils  • lupines  • peas  • pigeon peas  • other leguminous crops- growing of oil seeds such as:  • soya beans  • groundnuts  • castor bean  • linseed  • mustard seed  • niger seed  • rapeseed  • safflower seed  • sesame seed  • sunflower seed  • other oil seeds ', N'', N'', N'This class excludes:- growing of rice, see 01.12- growing of sweet corn, see 01.13- growing of maize for fodder, see 01.19- growing of oleaginous fruits, see 01.26 ', N'01.1 ', 3)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (5, 398485, 4, N'01.12 ', N'Growing of rice ', N'0112 ', N'This class includes:- growing of rice (including organic farming and the growing of genetically modified rice) ', N'', N'', N'', N'01.1 ', 3)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (6, 398486, 4, N'01.13 ', N'Growing of vegetables and melons, roots and tubers ', N'0113 ', N'This class includes:- growing of leafy or stem vegetables such as:  • artichokes  • asparagus  • cabbages  • cauliflower and broccoli  • lettuce and chicory  • spinach  • other leafy or stem vegetables- growing of fruit bearing vegetables such as:  • cucumbers and gherkins  • eggplants (aubergines)  • tomatoes  • watermelons  • cantaloupes  • other melons and fruit-bearing vegetables- growing of root, bulb or tuberous vegetables such as:  • carrots  • turnips  • garlic  • onions (incl. shallots)  • leeks and other alliaceous vegetables  • other root, bulb or tuberous vegetables- growing of mushrooms and truffles- growing of vegetable seeds, including sugar beet seeds, excluding other beet seeds- growing of sugar beet- growing of other vegetables- growing of roots and tubers such as:  • potatoes  • sweet potatoes  • cassava  • yams  • other roots and tubers ', N'', N'', N'This class excludes:- growing of chillies, peppers (capsicum sop.) and other spices and aromatic crops, see 01.28- growing of mushroom spawn, see 01.30 ', N'01.1 ', 3)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (7, 398487, 4, N'01.14 ', N'Growing of sugar cane ', N'0114 ', N'', N'', N'', N'This class excludes:- growing of sugar beet, see 01.13 ', N'01.1 ', 3)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (8, 398488, 4, N'01.15 ', N'Growing of tobacco ', N'0115 ', N'This class includes:- growing of unmanufactured tobacco ', N'', N'', N'This class excludes:- manufacture of tobacco products, see 12.00 ', N'01.1 ', 3)
