INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (9, 398489, 4, N'01.16 ', N'Growing of fibre crops ', N'0116 ', N'This class includes:- growing of cotton- growing of jute, kenaf and other textile bast fibres- growing of flax and true hemp- growing of sisal and other textile fibre of the genus agave- growing of abaca, ramie and other vegetable textile fibres- growing of other fibre crops ', N'', N'', N'', N'01.1 ', 3)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (10, 398490, 4, N'01.19 ', N'Growing of other non-perennial crops ', N'0119 ', N'This class includes the growing of all other non-perennial crops:- growing of swedes, mangolds, fodder roots, clover, alfalfa, sainfoin, fodder maize and other grasses, forage kale and similar forage products - growing of beet seeds (excluding sugar beet seeds) and seeds of forage plants- growing of flowers- production of cut flowers and flower buds- growing of flower seeds ', N'', N'', N'This class excludes:- growing of non-perennial spices, aromatic, drug and pharmaceutical crops, see 01.28 ', N'01.1 ', 3)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (11, 398491, 3, N'01.2 ', N'Growing of perennial crops ', N'012 ', N'This group includes the growing of perennial crops, i.e. plants that lasts for more than two growing seasons, either dying back after each season or growing continuously. Included is the growing of these plants for the purpose of seed production. ', N'', N'', N'', N'01 ', 2)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (12, 398492, 4, N'01.21 ', N'Growing of grapes ', N'0121 ', N'This class includes:- growing of wine grapes and table grapes in vineyards ', N'', N'', N'This class excludes:- manufacture of wine, see 11.02 ', N'01.2 ', 11)
