INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (13, 398493, 4, N'01.22 ', N'Growing of tropical and subtropical fruits ', N'0122 ', N'This class includes:- growing of tropical and subtropical fruits:  • avocados  • bananas and plantains  • dates  • figs  • mangoes  • papayas  • pineapples  • other tropical and subtropical fruits ', N'', N'', N'', N'01.2 ', 11)
