INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (14, 398494, 4, N'01.23 ', N'Growing of citrus fruits ', N'0123 ', N'This class includes:- growing of citrus fruits:  • grapefruit and pomelo  • lemons and limes  • oranges  • tangerines, mandarins and clementines  • other citrus fruits ', N'', N'', N'', N'01.2 ', 11)
