INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (15, 398495, 4, N'01.24 ', N'Growing of pome fruits and stone fruits ', N'0124 ', N'This class includes:- growing of pome fruits and stone fruits:  • apples  • apricots  • cherries and sour cherries  • peaches and nectarines  • pears and quinces  • plums and sloes  • other pome fruits and stone fruits ', N'', N'', N'', N'01.2 ', 11)
