INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (16, 398496, 4, N'01.25 ', N'Growing of other tree and bush fruits and nuts ', N'0125 ', N'This class includes:- growing of berries:  • blueberries  • currants  • gooseberries  • kiwi fruit  • raspberries  • strawberries  • other berries- growing of fruit seeds- growing of edible nuts:  • almonds  • cashew nuts  • chestnuts  • hazelnuts  • pistachios  • walnuts  • other nuts- growing of other tree and bush fruits:  • locust beans ', N'', N'', N'This class excludes:- growing of coconuts, see 01.26 ', N'01.2 ', 11)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (17, 398497, 4, N'01.26 ', N'Growing of oleaginous fruits ', N'0126 ', N'This class includes:- growing of oleaginous fruits:  • coconuts  • olives  • oil palms  • other oleaginous fruits ', N'', N'', N'This class excludes:- growing of soya beans, groundnuts and other oil seeds, see 01.11 ', N'01.2 ', 11)
