INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (18, 398498, 4, N'01.27 ', N'Growing of beverage crops ', N'0127 ', N'This class includes:- growing of beverage crops:  • coffee  • tea  • mate  • cocoa   • other beverage crops ', N'', N'', N'', N'01.2 ', 11)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (19, 398499, 4, N'01.28 ', N'Growing of spices, aromatic, drug and pharmaceutical crops ', N'0128 ', N'This class includes:- growing of perennial and non-perennial spices and aromatic crops:  • pepper (piper sop.)  • chillies and peppers (capsicum sop.)  • nutmeg, mace and cardamoms  • anise, badian and fennel  • cinnamon (canella)  • cloves  • ginger  • vanilla  • hops  • other spices and aromatic crops- growing of drug and narcotic crops ', N'', N'', N'', N'01.2 ', 11)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (20, 398500, 4, N'01.29 ', N'Growing of other perennial crops ', N'0129 ', N'This class includes:- growing of rubber trees for harvesting of latex- growing of Christmas trees- growing of trees for extraction of sap- growing of vegetable materials of a kind used primarily for plaiting ', N'', N'', N'This class excludes:- growing of flowers, production of cut flower buds and growing of flower seeds, see 01.19- gathering of tree sap or rubber-like gums in the wild, see 02.30 ', N'01.2 ', 11)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (21, 398501, 3, N'01.3 ', N'Plant propagation ', N'013 ', N'', N'', N'', N'', N'01 ', 2)
