INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (22, 398502, 4, N'01.30 ', N'Plant propagation ', N'0130 ', N'This class includes the production of all vegetative planting materials including cuttings, suckers and seedlings for direct plant propagation or to create plant grafting stock into which selected scion is grafted for eventual planting to produce crops.This class includes:- growing of plants for planting- growing of plants for ornamental purposes, including turf for transplanting- growing of live plants for bulbs, tubers and roots; cuttings and slips; mushroom spawn - operation of tree nurseries, except forest tree nurseries ', N'', N'- Nurturing and selling of tree seedlings ', N'This class excludes:- growing of plants for the purpose of seed production, see 01.1, 01.2- operation of forest tree nurseries, see 02.10 ', N'01.3 ', 21)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (23, 398503, 3, N'01.4 ', N'Animal production ', N'014 ', N'This group includes raising (farming) and breeding of all animals, except aquatic animals. ', N'', N'', N'This group excludes: - farm animal boarding and care, see 01.62- production of hides and skins from slaughterhouses, see 10.11 ', N'01 ', 2)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (24, 398504, 4, N'01.41 ', N'Raising of dairy cattle ', N'0141 ', N'This class includes:- raising and breeding of dairy cattle- production of raw milk from cows or buffaloes ', N'', N'', N'This class excludes:- processing of milk, see 10.51 ', N'01.4 ', 23)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (25, 398505, 4, N'01.42 ', N'Raising of other cattle and buffaloes ', N'0141 ', N'This class includes:- raising and breeding of cattle and buffaloes for meat- production of bovine semen ', N'', N'', N'', N'01.4 ', 23)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (26, 398506, 4, N'01.43 ', N'Raising of horses and other equines ', N'0142 ', N'This class includes:- raising and breeding of horses, asses, mules or hinnies ', N'', N'', N'This class excludes:- operation of racing and riding stables, see 93.19 ', N'01.4 ', 23)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (27, 398507, 4, N'01.44 ', N'Raising of camels and camelids ', N'0143 ', N'This class includes:- raising and breeding of camels (dromedary) and camelids ', N'', N'', N'', N'01.4 ', 23)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (28, 398508, 4, N'01.45 ', N'Raising of sheep and goats ', N'0144 ', N'This class includes:- raising and breeding of sheep and goats- production of raw sheep or goat milk- production of raw wool ', N'', N'', N'This class excludes:- sheep shearing on a fee or contract basis, see 01.62- production of pulled wool, see 10.11- processing of milk, see 10.51 ', N'01.4 ', 23)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (29, 398509, 4, N'01.46 ', N'Raising of swine/pigs ', N'0145 ', N'This class includes:- raising and breeding of swine (pigs) ', N'', N'', N'', N'01.4 ', 23)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (30, 398510, 4, N'01.47 ', N'Raising of poultry ', N'0146 ', N'This class includes:- raising and breeding of poultry:  • chickens, turkeys, ducks, geese and guinea fowls- production of eggs from poultry- operation of poultry hatcheries ', N'', N'', N'This class excludes:- production of feathers or down, see 10.12 ', N'01.4 ', 23)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (31, 398511, 4, N'01.49 ', N'Raising of other animals ', N'0149 ', N'This class includes:- raising and breeding of semi-domesticated or other live animals:  • ostriches and emus  • other birds (except poultry)  • insects  • rabbits and other fur animals- production of fur skins, reptile or bird skins from ranching operation- operation of worm farms, land mollusc farms, snail farms etc.- raising of silk worms, production of silk worm cocoons- bee-keeping and production of honey and beeswax- raising and breeding of pet animals (except fish):  • cats and dogs  • birds, such as parakeets etc.  • hamsters etc.- raising of diverse animals ', N'', N'- Production of fresh quail eggs and quail egg for hatching ', N'This class excludes:- production of hides and skins originating from hunting and trapping, see 01.70- operation of frog farms, crocodile farms, marine worm farms, see 03.21, 03.22- operation of fish farms, see 03.21, 03.22- boarding and training of pet animals, see 96.09- raising and breeding of poultry, see 01.47 ', N'01.4 ', 23)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (32, 398512, 3, N'01.5 ', N'Mixed farming ', N'015 ', N'', N'', N'', N'', N'01 ', 2)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (33, 398513, 4, N'01.50 ', N'Mixed farming ', N'0150 ', N'This class includes the combined production of crops and animals without a specialised production of crops or animals. The size of the overall farming operation is not a determining factor. If either production of crops or animals in a given unit is 66% or more of standard gross margins, the combined activity should not be included here, but allocated to crop or animal farming. ', N'', N'', N'This class excludes:- mixed crop farming, see groups 01.1 and 01.2- mixed animal farming, see group 01.4 ', N'01.5 ', 32)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (34, 398514, 3, N'01.6 ', N'Support activities to agriculture and post-harvest crop activities ', N'016 ', N'This group includes activities incidental to agricultural production and activities similar to agriculture not undertaken for production purposes (in the sense of harvesting agricultural products), done on a fee or contract basis. ', N'Also included are post-harvest crop activities, aimed at preparing agricultural products for the primary market. ', N'', N'', N'01 ', 2)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (35, 398515, 4, N'01.61 ', N'Support activities for crop production ', N'0161 ', N'This class includes:- agricultural activities on a fee or contract basis:  • preparation of fields  • establishing a crop  • treatment of crops  • crop spraying, including by air  • trimming of fruit trees and vines  • transplanting of rice, thinning of beets  • harvesting  • pest control (including rabbits) in connection with agriculture- maintenance of agricultural land in good agricultural and environmental condition- operation of agricultural irrigation equipment ', N'This class also includes:- provision of agricultural machinery with operators and crew ', N'', N'This class excludes:- post-harvest crop activities, see 01.63- drainage of agricultural land, see 43.12- landscape architecture, see 71.11- activities of agronomists and agricultural economists, see 74.90- landscape gardening, planting, see 81.30- organisation of agricultural shows and fairs, see 82.30 ', N'01.6 ', 34)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (36, 398516, 4, N'01.62 ', N'Support activities for animal production ', N'0162 ', N'This class includes:- agricultural activities on a fee or contract basis:  • activities to promote propagation, growth and output of animals  • herd testing services, droving services, agistment services, poultry caponising, coop cleaning etc.  • activities related to artificial insemination  • stud services  • sheep shearing  • farm animal boarding and care ', N'This class also includes:- activities of farriers ', N'- Automated egg hatching for poultry- Operation of livestock management systems ', N'This class excludes:- provision of space for animal boarding only, see 68.20- veterinary activities, see 75.00- vaccination of animals, see 75.00- rental of animals (e.g. herds), see 77.39- pet boarding, see 96.09 ', N'01.6 ', 34)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (37, 398517, 4, N'01.63 ', N'Post-harvest crop activities ', N'0163 ', N'This class includes:- preparation of crops for primary markets, i.e. cleaning, trimming, grading, disinfecting- cotton ginning- preparation of tobacco leaves, e.g. drying- preparation of cocoa beans, e.g. peeling- waxing of fruit- sun-drying of fruit and vegetables ', N'', N'- Sun-drying of tobacco- Sun-drying of herbs and spices ', N'This class excludes:- preparation of agricultural products by the producer, see corresponding class in groups 01.1, 01.2 or 01.3- post-harvest activities aimed at improving the propagation quality of seed, see 01.64 - stemming and redrying of tobacco, see 12.00- marketing activities of commission merchants and cooperative associations, see division 46- wholesale of agricultural raw materials, see 46.2 ', N'01.6 ', 34)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (38, 398518, 4, N'01.64 ', N'Seed processing for propagation ', N'0164 ', N'This class includes all post-harvest activities aimed at improving the propagation quality of seed through the removal of non-seed materials, undersized, mechanically or insect-damaged and immature seeds as well as removing the seed moisture to a safe level for seed storage. This activity includes the drying, cleaning, grading and treating of seeds until they are marketed. The treatment of genetically modified seeds is included here. ', N'', N'', N'This class excludes:- growing of seeds, see groups 01.1 and 01.2- processing of seeds to obtain oil, see 10.41- research to develop or modify new forms of seeds, see 72.11 ', N'01.6 ', 34)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (39, 398519, 3, N'01.7 ', N'Hunting, trapping and related service activities ', N'017 ', N'', N'', N'', N'', N'01 ', 2)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (40, 398520, 4, N'01.70 ', N'Hunting, trapping and related service activities ', N'0170 ', N'This class includes:- hunting and trapping on a commercial basis- taking of animals (dead or alive) for food, fur, skin, or for use in research, in zoos or as pets- production of fur skins, reptile or bird skins from hunting or trapping activities ', N'This class also includes:- land-based catching of sea mammals such as walrus and seal ', N'- Catching of frogs in the wild ', N'This class excludes:- production of fur skins, reptile or bird skins from ranching operations, see group 01.49- raising of game animals on ranching operations, see 01.4- catching of whales, see 03.11- production of hides and skins originating from slaughterhouses, see 10.11- hunting for sport or recreation and related service activities, see 93.19- service activities to promote hunting and trapping, see 94.99 ', N'01.7 ', 39)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (41, 398521, 2, N'02 ', N'Forestry and logging ', N'02 ', N'This division includes the production of roundwood as well as the extraction and gathering of wild growing non-wood forest products. Besides the production of timber, forestry activities result in products that undergo little processing, such as firewood, charcoal and roundwood used in an unprocessed form (e.g. pit-props, pulpwood etc.). These activities can be carried out in natural or planted forests. ', N'', N'', N'Excluded is further processing of wood beginning with sawmilling and planing of wood, see division 16. ', N'A ', 1)
GO
