INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (43, 398523, 4, N'02.10 ', N'Silviculture and other forestry activities ', N'0210 ', N'This class includes:- growing of standing timber: planting, replanting, transplanting, thinning and conserving of forests and timber tracts- growing of coppice, pulpwood and fire wood- operation of forest tree nurseriesThese activities can be carried out in natural or planted forests. ', N'', N'- Growing of paulownia and willow ', N'This class excludes:- growing of Christmas trees, see 01.29- operation of tree nurseries, except for forest trees, see 01.30- gathering of mushrooms and other wild growing non-wood forest products, see 02.30- production of wood chips and particles, see 16.10 ', N'02.1 ', 42)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (44, 398524, 3, N'02.2 ', N'Logging ', N'022 ', N'', N'', N'', N'', N'02 ', 41)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (45, 398525, 4, N'02.20 ', N'Logging ', N'0220 ', N'This class includes:- production of roundwood for forest-based manufacturing industries- production of roundwood used in an unprocessed form such as pit-props, fence posts and utility poles- gathering and production of wood for energy- gathering and production of forest harvesting residues for energy- production of charcoal in the forest (using traditional methods)The output of this activity can take the form of logs or fire wood. ', N'', N'', N'This class excludes:- growing of Christmas trees, see 01.29- growing of standing timber: planting, replanting, transplanting, thinning and conserving of forests and timber tracts, see 02.10- gathering of wild growing non-wood forest products, see 02.30- production of wood chips and particles, see 16.10- production of charcoal through distillation of wood, see 20.14 ', N'02.2 ', 44)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (46, 398526, 3, N'02.3 ', N'Gathering of wild growing non-wood products ', N'023 ', N'', N'', N'', N'', N'02 ', 41)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (47, 398527, 4, N'02.30 ', N'Gathering of wild growing non-wood products ', N'0230 ', N'This class includes:- gathering of wild growing materials:  • mushrooms, truffles  • berries  • nuts  • balata and other rubber-like gums  • cork  • lac and resins  • balsams  • vegetable hair  • eelgrass  • acorns, horse chestnuts  • mosses and lichens ', N'', N'', N'This class excludes:- managed production of any of these products (except growing of cork trees), see division 01- growing of mushrooms or truffles, see 01.13- growing of berries or nuts, see 01.25- gathering of fire wood, see 02.20- production of wood chips, see 16.10 ', N'02.3 ', 46)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (48, 398528, 3, N'02.4 ', N'Support services to forestry ', N'024 ', N'', N'', N'', N'', N'02 ', 41)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (49, 398529, 4, N'02.40 ', N'Support services to forestry ', N'0240 ', N'This class includes carrying out part of the forestry operation on a fee or contract basis.This class includes:- forestry service activities:  • forestry inventories  • forest management consulting services  • timber evaluation  • forest fire fighting and protection  • forest pest control- logging service activities:  • transport of logs within the forest ', N'', N'', N'This class excludes:- operation of forest tree nurseries, see 02.10- draining of forestry land, see 43.12- clearing of building sites, see 43.12 ', N'02.4 ', 48)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (50, 398530, 2, N'03 ', N'Fishing and aquaculture ', N'03 ', N'This division includes capture fishery and aquaculture, covering the use of fishery resources from marine, brackish or freshwater environments, with the goal of capturing or gathering fish, crustaceans, molluscs and other marine organisms and products (e.g. aquatic plants, pearls, sponges etc). ', N'Also included are activities that are normally integrated in the process of production for own account (e.g. seeding oysters for pearl production). Service activities incidental to marine or freshwater fishery or aquaculture are included in the related fishing or aquaculture activities. ', N'', N'This division does not include building and repairing of ships and boats (30.1, 33.15) and sport or recreational fishing activities (93.19). Processing of fish, crustaceans or molluscs is excluded, whether at land-based plants or on factory ships (10.20). ', N'A ', 1)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (51, 398531, 3, N'03.1 ', N'Fishing ', N'031 ', N'This group includes "capture fishery", i.e. the hunting, collecting and gathering activities directed at removing or collecting live wild aquatic organisms (predominantly fish, molluscs and crustaceans) including plants from the oceanic, coastal or inland waters for human consumption and other purposes by hand or more usually by various types of fishing gear such as nets, lines and stationary traps. Such activities can be conducted on the intertidal shoreline (e.g. collection of molluscs such as mussels and oysters) or shore based netting, or from home-made dugouts or more commonly using commercially made boats in inshore, coastal waters or offshore waters. Such activities also include fishing in restocked water bodies. ', N'', N'', N'', N'03 ', 50)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (52, 398532, 4, N'03.11 ', N'Marine fishing ', N'0311 ', N'This class includes:- fishing on a commercial basis in ocean and coastal waters- taking of marine crustaceans and molluscs- whale catching- taking of marine aquatic animals: turtles, sea squirts, tunicates, sea urchins etc. ', N'This class also includes:- activities of vessels engaged both in marine fishing and in processing and preserving of fish- gathering of other marine organisms and materials: natural pearls, sponges, coral and algae ', N'', N'This class excludes:- capturing of marine mammals, except whales, e.g. walruses, seals, see 01.70- processing of whales on factory ships, see 10.11- processing of fish, crustaceans and molluscs on factory ships or in factories ashore, see 10.20- rental of pleasure boats with crew for sea and coastal water transport (e.g. for fishing cruises), see 50.10- fishing inspection, protection and patrol services, see 84.24- fishing practiced for sport or recreation and related services, see 93.19- operation of sport fishing preserves, see 93.19 ', N'03.1 ', 51)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (53, 398533, 4, N'03.12 ', N'Freshwater fishing ', N'0312 ', N'This class includes:- fishing on a commercial basis in inland waters- taking of freshwater crustaceans and molluscs- taking of freshwater aquatic animals ', N'This class also includes:- gathering of freshwater materials ', N'', N'This class excludes:- processing of fish, crustaceans and molluscs, see 10.20- fishing inspection, protection and patrol services, see 84.24- fishing practiced for sport or recreation and related services, see 93.19- operation of sport fishing preserves, see 93.19 ', N'03.1 ', 51)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (54, 398534, 3, N'03.2 ', N'Aquaculture ', N'032 ', N'This group includes "aquaculture" (or aquafarming), i.e. the production process involving the culturing or farming (including harvesting) of aquatic organisms (fish, molluscs, crustaceans, plants, crocodiles, alligators and amphibians) using techniques designed to increase the production of the organisms in question beyond the natural capacity of the environment (for example regular stocking, feeding and protection from predators). Culturing/farming refers to the rearing up to their juvenile and/or adult phase under captive conditions of the above organisms. ', N'In addition, "aquaculture" also encompasses individual, corporate or state ownership of the individual organisms throughout the rearing or culture stage, up to and including harvesting. ', N'', N'', N'03 ', 50)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (55, 398535, 4, N'03.21 ', N'Marine aquaculture ', N'0321 ', N'This class includes:- fish farming in sea water including farming of marine ornamental fish- production of bivalve spat (oyster mussel etc.), lobsterlings, shrimp post-larvae, fish fry and fingerlings- growing of laver and other edible seaweeds- culture of crustaceans, bivalves, other molluscs and other aquatic animals in sea water ', N'This class also includes:- aquaculture activities in brackish waters- aquaculture activities in salt water filled tanks and reservoirs- operation of fish hatcheries (marine)- operation of marine worm farms ', N'', N'This class excludes:- frog farming, see 03.22- operation of sport fishing preserves, see 93.19 ', N'03.2 ', 54)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (56, 398536, 4, N'03.22 ', N'Freshwater aquaculture ', N'0322 ', N'This class includes:- fish farming in freshwater including farming of freshwater ornamental fish- culture of freshwater crustaceans, bivalves, other molluscs and other aquatic animals- operation of fish hatcheries (freshwater)- farming of frogs ', N'', N'- Farming of frogs in freshwater ', N'This class excludes:- aquaculture activities in salt water filled tanks and reservoirs, see 03.21- operation of sport fishing preserves, see 93.19 ', N'03.2 ', 54)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (57, 398537, 1, N'B ', N'MINING AND QUARRYING ', N'B ', N'Mining and quarrying include the extraction of minerals occurring naturally as solids (coal and ores), liquids (petroleum) or gases (natural gas). Extraction can be achieved by different methods such as underground or surface mining, well operation, seabed mining etc.This section includes supplementary activities aimed at preparing the crude materials for marketing, for example, crushing, grinding, cleaning, drying, sorting, concentrating ores, liquefaction of natural gas and agglomeration of solid fuels. These operations are often accomplished by the units that extracted the resource and/or others located nearby.Mining activities are classified into divisions, groups and classes on the basis of the principal mineral produced. Divisions 05, 06 are concerned with mining and quarrying of fossil fuels (coal, lignite, petroleum, gas); divisions 07, 08 concern metal ores, various minerals and quarry products. Some of the technical operations of this section, particularly related to the extraction of hydrocarbons, may also be carried out for third parties by specialised units as an industrial service which is reflected in division 09. ', N'', N'', N'This section excludes:- processing of the extracted materials, see section C (Manufacturing)- usage of the extracted materials without a further transformation for construction purposes, see section F (Construction)- bottling of natural spring and mineral waters at springs and wells, see 11.07- crushing, grinding or otherwise treating certain earths, rocks and minerals not carried on in conjunction with mining and quarrying, see 23.9 ', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (58, 398538, 2, N'05 ', N'Mining of coal and lignite ', N'05 ', N'This division includes the extraction of solid mineral fuels through underground or open-cast mining and includes operations (e.g. grading, cleaning, compressing and other steps necessary for transportation etc.) leading to a marketable product. ', N'', N'', N'This division does not include coking (see 19.10), services incidental to coal or lignite mining (see 09.90) or the manufacture of briquettes (see 19.20). ', N'B ', 57)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (59, 398539, 3, N'05.1 ', N'Mining of hard coal ', N'051 ', N'', N'', N'', N'', N'05 ', 58)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (60, 398540, 4, N'05.10 ', N'Mining of hard coal ', N'0510 ', N'This class includes:- mining of hard coal: underground or surface mining, including mining through liquefaction methods- cleaning, sizing, grading, pulverising, compressing etc. of coal to classify, improve quality or facilitate transport or storage ', N'This class also includes:- recovery of hard coal from culm banks ', N'', N'This class excludes:- lignite mining, see 05.20- peat digging, see 08.92- support activities for hard coal mining, see 09.90- test drilling for coal mining, see 09.90- coke ovens producing solid fuels, see 19.10- manufacture of hard coal briquettes, see 19.20- work performed to develop or prepare properties for coal mining, see 43.12 ', N'05.1 ', 59)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (61, 398541, 3, N'05.2 ', N'Mining of lignite ', N'052 ', N'', N'', N'', N'', N'05 ', 58)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (62, 398542, 4, N'05.20 ', N'Mining of lignite ', N'0520 ', N'This class includes:- mining of lignite (brown coal): underground or surface mining, including mining through liquefaction methods- washing, dehydrating, pulverising, compressing of lignite to improve quality or facilitate transport or storage ', N'', N'', N'This class excludes:- hard coal mining, see 05.10- peat digging, see 08.92- support activities for lignite mining, see 09.90- test drilling for coal mining, see 09.90- manufacture of lignite fuel briquettes, see 19.20- work performed to develop or prepare properties for coal mining, see 43.12 ', N'05.2 ', 61)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (63, 398543, 2, N'06 ', N'Extraction of crude petroleum and natural gas ', N'06 ', N'This division includes the production of crude petroleum, the mining and extraction of oil from oil shale and oil sands and the production of natural gas and recovery of hydrocarbon liquids. This division includes the activities of operating and/or developing oil and gas field properties. Such activities may include drilling, completing and equipping wells; operating separators, emulsion breakers, desalting equipment and field gathering lines for crude petroleum; and all other activities in the preparation of oil and gas up to the point of shipment from the producing property. ', N'', N'', N'This division excludes:- oil and gas field services, performed on a fee or contract basis, see 09.10- oil and gas well exploration, see 09.10- test drilling and boring, see 09.10- refining of petroleum products, see 19.20- geophysical, geologic and seismic surveying, see 71.12 ', N'B ', 57)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (64, 398544, 3, N'06.1 ', N'Extraction of crude petroleum ', N'061 ', N'', N'', N'', N'', N'06 ', 63)
