INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (65, 398545, 4, N'06.10 ', N'Extraction of crude petroleum ', N'0610 ', N'This class includes:- extraction of crude petroleum oils ', N'This class also includes:- extraction of bituminous or oil shale and tar sand- production of crude petroleum from bituminous shale and sand- processes to obtain crude oils: decantation, desalting, dehydration, stabilisation etc. ', N'', N'This class excludes:- support activities for oil and natural gas extraction, see 09.10- oil and gas exploration, see 09.10- manufacture of refined petroleum products, see 19.20- recovery of liquefied petroleum gases in the refining of petroleum, see 19.20- operation of pipelines, see 49.50 ', N'06.1 ', 64)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (66, 398546, 3, N'06.2 ', N'Extraction of natural gas ', N'062 ', N'', N'', N'', N'', N'06 ', 63)
