INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (67, 398547, 4, N'06.20 ', N'Extraction of natural gas ', N'0620 ', N'This class includes:- production of crude gaseous hydrocarbon (natural gas)- extraction of condensates- draining and separation of liquid hydrocarbon fractions- gas desulphurisation ', N'This class also includes:- mining of hydrocarbon liquids, obtained through liquefaction or pyrolysis ', N'- Extraction of coal mine methane ', N'This class excludes:- support activities for oil and natural gas extraction, see 09.10- oil and gas exploration, see 09.10- recovery of liquefied petroleum gases in the refining of petroleum, see 19.20- manufacture of industrial gases, see 20.11- operation of pipelines, see 49.50 ', N'06.2 ', 66)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (68, 398548, 2, N'07 ', N'Mining of metal ores ', N'07 ', N'This division includes mining for metallic minerals (ores), performed through underground or open-cast extraction, seabed mining etc. ', N'This division also includes:- ore dressing and beneficiating operations, such as crushing, grinding, washing, drying, sintering, calcining or leaching ore, gravity separation or flotation operations ', N'', N'This division excludes:- roasting of iron pyrites, see 20.13- production of aluminium oxide, see 24.42- operation of blast furnaces, see division 24 ', N'B ', 57)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (69, 398549, 3, N'07.1 ', N'Mining of iron ores ', N'071 ', N'', N'', N'', N'', N'07 ', 68)
