INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (70, 398550, 4, N'07.10 ', N'Mining of iron ores ', N'0710 ', N'This class includes:- mining of ores valued chiefly for iron content- beneficiation and agglomeration of iron ores ', N'', N'', N'This class excludes:- extraction and preparation of pyrites and pyrrhotite (except roasting), see 08.91 ', N'07.1 ', 69)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (71, 398551, 3, N'07.2 ', N'Mining of non-ferrous metal ores ', N'072 ', N'This group includes the mining of non-ferrous metal ores. ', N'', N'', N'', N'07 ', 68)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (72, 398552, 4, N'07.21 ', N'Mining of uranium and thorium ores ', N'0721 ', N'This class includes:- mining of ores chiefly valued for uranium and thorium content: pitchblende etc.- concentration of such ores- manufacture of yellowcake ', N'', N'', N'This class excludes:- enrichment of uranium and thorium ores, see 20.13- production of uranium metal from pitchblende or other ores, see 24.46- smelting and refining of uranium, see 24.46 ', N'07.2 ', 71)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (73, 398553, 4, N'07.29 ', N'Mining of other non-ferrous metal ores ', N'0729 ', N'This class includes:- mining and preparation of ores chiefly valued for non-ferrous metal content:  • aluminium (bauxite), copper, lead, zinc, tin, manganese, chrome, nickel, cobalt, molybdenum, tantalum, vanadium etc.  • precious metals: gold, silver, platinum ', N'', N'', N'This class excludes:- mining and preparation of uranium and thorium ores, see 07.21- production of aluminium oxide, see 24.42- production of mattes of copper or of nickel, see 24.44, 24.45 ', N'07.2 ', 71)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (74, 398554, 2, N'08 ', N'Other mining and quarrying ', N'08 ', N'This division includes extraction from a mine or quarry, but also dredging of alluvial deposits, rock crushing and the use of salt marshes. The products are used most notably in construction (e.g. sands, stones etc.), manufacture of materials (e.g. clay, gypsum, calcium etc.), manufacture of chemicals etc. ', N'', N'', N'This division does not include processing (except crushing, grinding, cutting, cleaning, drying, sorting and mixing) of the minerals extracted. ', N'B ', 57)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (75, 398555, 3, N'08.1 ', N'Quarrying of stone, sand and clay ', N'081 ', N'', N'', N'', N'', N'08 ', 74)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (76, 398556, 4, N'08.11 ', N'Quarrying of ornamental and building stone, limestone, gypsum, chalk and slate ', N'0810 ', N'This class includes:- quarrying, rough trimming and sawing of monumental and building stone such as marble, granite, sandstone etc.- breaking and crushing of ornamental and building stone- quarrying, crushing and breaking of limestone- mining of gypsum and anhydrite- mining of chalk and uncalcined dolomite ', N'', N'', N'This class excludes:- mining of chemical and fertiliser minerals, see 08.91- production of calcined dolomite, see 23.52- cutting, shaping and finishing of stone outside quarries, see 23.70 ', N'08.1 ', 75)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (77, 398557, 4, N'08.12 ', N'Operation of gravel and sand pits; mining of clays and kaolin ', N'0810 ', N'This class includes:- extraction and dredging of industrial sand, sand for construction and gravel- breaking and crushing of gravel - quarrying of sand- mining of clays, refractory clays and kaolin ', N'', N'- Crushing and breaking of gravel off mine site ', N'This class excludes:- mining of bituminous sand, see 06.10 ', N'08.1 ', 75)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (78, 398558, 3, N'08.9 ', N'Mining and quarrying n.e.c. ', N'089 ', N'', N'', N'', N'', N'08 ', 74)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (79, 398559, 4, N'08.91 ', N'Mining of chemical and fertiliser minerals ', N'0891 ', N'This class includes:- mining of natural phosphates and natural potassium salts- mining of native sulphur- extraction and preparation of pyrites and pyrrhotite, except roasting- mining of natural barium sulphate and carbonate (barytes and witherite), natural borates, natural magnesium sulphates (kieserite)- mining of earth colours, fluorspar and other minerals valued chiefly as a source of chemicals ', N'This class also includes:- guano mining ', N'', N'This class excludes:- extraction of salt, see 08.93- roasting of iron pyrites, see 20.13- manufacture of synthetic fertilisers and nitrogen compounds, see 20.15 ', N'08.9 ', 78)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (80, 398560, 4, N'08.92 ', N'Extraction of peat ', N'0892 ', N'This class includes:- peat digging- preparation of peat to improve quality or facilitate transport or storage ', N'', N'', N'This class excludes:- service activities incidental to peat mining, see 09.90- manufacture of peat briquettes, see 19.20- manufacture of potting soil mixtures of peat, natural soil, sands, clays, fertiliser minerals etc., see 20.15- manufacture of articles of peat, see 23.99 ', N'08.9 ', 78)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (81, 398561, 4, N'08.93 ', N'Extraction of salt ', N'0893 ', N'This class includes:- extraction of salt from underground including by dissolving and pumping- salt production by evaporation of sea water or other saline waters- crushing, purification and refining of salt by the producer ', N'', N'', N'This class excludes:- processing of salt into food-grade salt, e.g. iodised salt, see 10.84- potable water production by evaporation of saline water, see 36.00 ', N'08.9 ', 78)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (82, 398562, 4, N'08.99 ', N'Other mining and quarrying n.e.c. ', N'0899 ', N'This class includes:- mining and quarrying of various minerals and materials:  • abrasive materials, asbestos, siliceous fossil meals, natural graphite, steatite (talc), feldspar etc.  • natural asphalt, asphaltites and asphaltic rock; natural solid bitumen  • gemstones, quartz, mica etc. ', N'', N'', N'', N'08.9 ', 78)
