INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (117, 398597, 4, N'10.84 ', N'Manufacture of condiments and seasonings ', N'1079 ', N'This class includes:- manufacture of spices, sauces and condiments:  • mayonnaise  • mustard flour and meal  • prepared mustard etc.- manufacture of vinegar ', N'This class also includes:- processing of salt into food-grade salt, e.g. iodised salt ', N'- Drying of herbs and spices, using industrial methods, where herbs and spices are lyophilised, frozen and dried in this condition ', N'This class excludes:- growing of spice crops, see 01.28 ', N'10.8 ', 113)
