INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (162, 398642, 4, N'14.39 ', N'Manufacture of other knitted and crocheted apparel ', N'1430 ', N'This class includes:- manufacture of knitted or crocheted wearing apparel and other made-up articles directly into shape: pullovers, cardigans, jerseys, waistcoats and similar articles ', N'', N'', N'This class excludes:- manufacture of knitted and crocheted fabrics, see 13.91- manufacture of hosiery, see 14.31 ', N'14.3 ', 160)
