INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (164, 398644, 3, N'15.1 ', N'Tanning and dressing of leather; manufacture of luggage, handbags, saddlery and harness; dressing and dyeing of fur ', N'151 ', N'This group includes the manufacture of leather and fur and products thereof. ', N'', N'', N'', N'15 ', 163)
