INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (165, 398645, 4, N'15.11 ', N'Tanning and dressing of leather; dressing and dyeing of fur ', N'1511 ', N'This class includes:- tanning, dyeing and dressing of hides and skins- manufacture of chamois dressed, parchment dressed, patent or metallised leathers- manufacture of composition leather- scraping, shearing, plucking, currying, tanning, bleaching and dyeing of fur skins and hides with the hair on ', N'', N'', N'This class excludes:- production of hides and skins as part of ranching, see 01.4- production of hides and skins as part of slaughtering, see 10.11- manufacture of leather apparel, see 14.11- manufacture of imitation leather not based on natural leather, see 22.19, 22.29 ', N'15.1 ', 164)
