INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (168, 398648, 4, N'15.20 ', N'Manufacture of footwear ', N'1520 ', N'This class includes:- manufacture of footwear for all purposes, of any material, by any process, including moulding (see below for exceptions)- manufacture of leather parts of footwear: manufacture of uppers and parts of uppers, outer and inner soles, heels etc.- manufacture of gaiters, leggings and similar articles ', N'', N'', N'This class excludes:- manufacture of footwear of textile material without applied soles, see 14.19- manufacture of wooden shoe parts (e.g. heels and lasts), see 16.29- manufacture of rubber boot and shoe heels and soles and other rubber footwear parts, see 22.19- manufacture of plastic footwear parts, see 22.29- manufacture of ski-boots, see 32.30- manufacture of orthopaedic shoes, see 32.50 ', N'15.2 ', 167)
