INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (169, 398649, 2, N'16 ', N'Manufacture of wood and of products of wood and cork, except furniture; manufacture of articles of straw and plaiting materials ', N'16 ', N'This division includes the manufacture of wood products, such as lumber, plywood, veneers, wood containers, wood flooring, wood trusses, and prefabricated wood buildings. The production processes include sawing, planing, shaping, laminating, and assembling of wood products starting from logs that are cut into bolts, or lumber that may then be cut further, or shaped by lathes or other shaping tools. The lumber or other transformed wood shapes may also be subsequently planed or smoothed, and assembled into finished products, such as wood containers.With the exception of sawmilling, this division is subdivided mainly based on the specific products manufactured. ', N'', N'', N'This division does not include the manufacture of furniture (31.0), or the installation of wooden fittings and the like (43.32, 43.33, 43.39). ', N'C ', 88)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (170, 398650, 3, N'16.1 ', N'Sawmilling and planing of wood ', N'161 ', N'', N'', N'', N'', N'16 ', 169)
