INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (171, 398651, 4, N'16.10 ', N'Sawmilling and planing of wood ', N'1610 ', N'This class includes:- sawing, planing and machining of wood- slicing, peeling or chipping logs- manufacture of wooden railway sleepers- manufacture of unassembled wooden flooring- manufacture of wood wool, wood flour, chips, particles ', N'This class also includes:- drying of wood- impregnation or chemical treatment of wood with preservatives or other materials ', N'', N'This class excludes:- logging and production of wood in the rough, see 02.20- manufacture of veneer sheets thin enough for use in plywood, boards and panels, see 16.21- manufacture of shingles and shakes, beadings and mouldings, see 16.23- manufacture of fire logs or pressed wood, see 16.29 ', N'16.1 ', 170)
