INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (172, 398652, 3, N'16.2 ', N'Manufacture of products of wood, cork, straw and plaiting materials ', N'162 ', N'This group includes the manufacture of products of wood, cork, straw or plaiting materials, including basic shapes as well as assembled products. ', N'', N'', N'', N'16 ', 169)
