INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (180, 398660, 4, N'17.11 ', N'Manufacture of pulp ', N'1701 ', N'This class includes:- manufacture of bleached, semi-bleached or unbleached paper pulp by mechanical, chemical (dissolving or non-dissolving) or semi-chemical processes- manufacture of cotton-linters pulp- removal of ink and manufacture of pulp from waste paper ', N'', N'', N'', N'17.1 ', 179)
