INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (183, 398663, 4, N'17.21 ', N'Manufacture of corrugated paper and paperboard and of containers of paper and paperboard ', N'1702 ', N'This class includes:- manufacture of corrugated paper and paperboard- manufacture of containers of corrugated paper or paperboard- manufacture of folding paperboard containers- manufacture of containers of solid board- manufacture of other containers of paper and paperboard- manufacture of sacks and bags of paper- manufacture of office box files and similar articles ', N'', N'', N'This class excludes:- manufacture of envelopes, see 17.23- manufacture of moulded or pressed articles of paper pulp (e.g. boxes for packing eggs, moulded pulp paper plates), see 17.29 ', N'17.2 ', 182)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (184, 398664, 4, N'17.22 ', N'Manufacture of household and sanitary goods and of toilet requisites ', N'1709 ', N'This class includes:- manufacture of household and personal hygiene paper and cellulose wadding products:  • cleansing tissues  • handkerchiefs, towels, serviettes  • toilet paper  • sanitary towels and tampons, napkins and napkin liners for babies  • cups, dishes and trays- manufacture of textile wadding and articles of wadding: sanitary towels, tampons etc. ', N'', N'', N'This class excludes:- manufacture of cellulose wadding, see 17.12 ', N'17.2 ', 182)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (185, 398665, 4, N'17.23 ', N'Manufacture of paper stationery ', N'1709 ', N'This class includes:- manufacture of printing and writing paper ready for use- manufacture of computer printout paper ready for use- manufacture of self-copy paper ready for use- manufacture of duplicator stencils and carbon paper ready for use- manufacture of gummed or adhesive paper ready for use- manufacture of envelopes and letter-cards- manufacture of educational and commercial stationery (notebooks, binders, registers, accounting books, business forms etc.), when the printed information is not the main characteristic- manufacture of boxes, pouches, wallets and writing compendiums containing an assortment of paper stationery ', N'', N'', N'This class excludes:- printing on paper products, see 18.1 ', N'17.2 ', 182)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (186, 398666, 4, N'17.24 ', N'Manufacture of wallpaper ', N'1709 ', N'This class includes:- manufacture of wallpaper and similar wall coverings, including vinyl-coated and textile wallpaper ', N'', N'', N'This class excludes:- manufacture of paper or paperboard in bulk, see 17.12- manufacture of plastic wallpaper, see 22.29 ', N'17.2 ', 182)
