INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (223, 398703, 4, N'20.60 ', N'Manufacture of man-made fibres ', N'2030 ', N'This class includes:- manufacture of synthetic or artificial filament tow- manufacture of synthetic or artificial staple fibres, not carded, combed or otherwise processed for spinning- manufacture of synthetic or artificial filament yarn, including high-tenacity yarn- manufacture of synthetic or artificial monofilament or strip ', N'', N'', N'This class excludes:- spinning of synthetic or artificial fibres, see 13.10- manufacture of yarns made of man-made staple, see 13.10 ', N'20.6 ', 222)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (224, 398704, 2, N'21 ', N'Manufacture of basic pharmaceutical products and pharmaceutical preparations ', N'21 ', N'This division includes the manufacture of basic pharmaceutical products and pharmaceutical preparations. ', N'This also includes the manufacture of medicinal chemical and botanical products. ', N'', N'', N'C ', 88)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (225, 398705, 3, N'21.1 ', N'Manufacture of basic pharmaceutical products ', N'210 ', N'', N'', N'', N'', N'21 ', 224)
