INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (226, 398706, 4, N'21.10 ', N'Manufacture of basic pharmaceutical products ', N'2100 ', N'This class includes:- manufacture of medicinal active substances to be used for their pharmacological properties in the manufacture of medicaments: antibiotics, basic vitamins, salicylic and O-acetylsalicylic acids etc.- processing of blood ', N'This class also includes:- manufacture of chemically pure sugars- processing of glands and manufacture of extracts of glands etc. ', N'- Manufacture of probiotics ', N'', N'21.1 ', 225)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (227, 398707, 3, N'21.2 ', N'Manufacture of pharmaceutical preparations ', N'210 ', N'', N'', N'', N'', N'21 ', 224)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (228, 398708, 4, N'21.20 ', N'Manufacture of pharmaceutical preparations ', N'2100 ', N'This class includes:- manufacture of medicaments:  • antisera and other blood fractions  • vaccines  • diverse medicaments, including homeopathic preparations- manufacture of chemical contraceptive products for external use and hormonal contraceptive medicaments- manufacture of medical diagnostic preparations, including pregnancy tests- manufacture of radioactive in-vivo diagnostic substances- manufacture of biotech pharmaceuticals ', N'This class also includes:- manufacture of medical impregnated wadding, gauze, bandages, dressings etc.- preparation of botanical products (grinding, grading, milling) for pharmaceutical use ', N'', N'This class excludes:- manufacture of herb infusions (mint, vervain, chamomile etc.), see 10.83- manufacture of dental fillings and dental cement, see 32.50- manufacture of bone reconstruction cements, see 32.50- manufacture of surgical drapes, see 32.50- wholesale of pharmaceuticals, see 46.46- retail sale of pharmaceuticals, see 47.73- research and development for pharmaceuticals and biotech pharmaceuticals, see 72.1- packaging of pharmaceuticals, see 82.92 ', N'21.2 ', 227)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (229, 398709, 2, N'22 ', N'Manufacture of rubber and plastic products ', N'22 ', N'This division includes the manufacture of rubber and plastics products. This division is characterised by the raw materials used in the manufacturing process. However, this does not imply that the manufacture of all products made of these materials is classified here. ', N'', N'', N'', N'C ', 88)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (230, 398710, 3, N'22.1 ', N'Manufacture of rubber products ', N'221 ', N'This group includes the manufacture of rubber products. ', N'', N'', N'', N'22 ', 229)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (231, 398711, 4, N'22.11 ', N'Manufacture of rubber tyres and tubes; retreading and rebuilding of rubber tyres ', N'2211 ', N'This class includes:- manufacture of rubber tyres for vehicles, equipment, mobile machinery, aircraft, toy, furniture and other uses:  • pneumatic tyres  • solid or cushion tyres- manufacture of inner tubes for tyres- manufacture of interchangeable tyre treads, tyre flaps, "camelback" strips for retreading tyres etc.- tyre rebuilding and retreading ', N'', N'', N'This class excludes:- manufacture of tube repair materials, see 22.19- tyre and tube repair, fitting or replacement, see 45.20 ', N'22.1 ', 230)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (232, 398712, 4, N'22.19 ', N'Manufacture of other rubber products ', N'2219 ', N'This class includes:- manufacture of other products of natural or synthetic rubber, unvulcanised, vulcanised or hardened:  • rubber plates, sheets, strip, rods, profile shapes  • tubes, pipes and hoses  • rubber conveyor or transmission belts or belting  • rubber hygienic articles: sheath contraceptives, teats, hot water bottles etc.  • rubber articles of apparel (if only sealed together, not sewn)  • rubber sole and other rubber parts of footwear  • rubber thread and rope  • rubberised yarn and fabrics  • rubber rings, fittings and seals  • rubber roller coverings  • inflatable rubber mattresses  • inflatable balloons- manufacture of rubber brushes- manufacture of hard rubber pipe stems- manufacture of hard rubber combs, hair pins, hair rollers, and similar ', N'This class also includes:- manufacture of rubber repair materials- manufacture of textile fabric impregnated, coated, covered or laminated with rubber, where rubber is the chief constituent- manufacture of rubber waterbed mattresses- manufacture of rubber bathing caps and aprons- manufacture of rubber wet suits and diving suits- manufacture of rubber sex articles ', N'- Manufacture of apparel made of latex ', N'This class excludes:- manufacture of tyre cord fabrics, see 13.96- manufacture of apparel of elastic fabrics, see 14.14, 14.19- manufacture of rubber footwear, see 15.20- manufacture of glues and adhesives based on rubber, see 20.52- manufacture of "camelback" strips, see 22.11- manufacture of inflatable rafts and boats, see 30.11, 30.12- manufacture of mattresses of uncovered cellular rubber, see 31.03- manufacture of rubber sports requisites, except apparel, see 32.30- manufacture of rubber games and toys (including children''s wading pools, inflatable children rubber boats, inflatable rubber animals, balls and the like), see 32.40- reclaiming of rubber, see 38.32 ', N'22.1 ', 230)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (233, 398713, 3, N'22.2 ', N'Manufacture of plastic products ', N'222 ', N'This group comprises processing new or spent (i.e., recycled) plastics resins into intermediate or final products, using such processes as compression moulding; extrusion moulding; injection moulding; blow moulding; and casting. For most of these, the production process is such that a wide variety of products can be made. ', N'', N'', N'', N'22 ', 229)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (234, 398714, 4, N'22.21 ', N'Manufacture of plastic plates, sheets, tubes and profiles ', N'2220 ', N'This class includes:- manufacture of semi-manufactures of plastic products:  • plastic plates, sheets, blocks, film, foil, strip etc. (whether self-adhesive or not)- manufacture of finished plastic products:  • plastic tubes, pipes and hoses; hose and pipe fittings  • cellophane film or sheet ', N'', N'', N'This class excludes:- manufacture of plastics in primary forms, see 20.16- manufacture of articles of synthetic or natural rubber, see 22.1 ', N'22.2 ', 233)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (235, 398715, 4, N'22.22 ', N'Manufacture of plastic packing goods ', N'2220 ', N'This class includes:- manufacture of plastic articles for the packing of goods:  • plastic bags, sacks, containers, boxes, cases, carboys, bottles etc. ', N'', N'', N'This class excludes:- manufacture of luggage and handbags of plastic, see 15.12 ', N'22.2 ', 233)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (236, 398716, 4, N'22.23 ', N'Manufacture of builders’ ware of plastic ', N'2220 ', N'This class includes:- manufacture of builders'' plastics ware:  • plastic doors, windows, frames, shutters, blinds, skirting boards  • tanks, reservoirs  • plastic floor, wall or ceiling coverings in rolls or in the form of tiles etc.  • plastic sanitary ware like plastic baths, shower baths, washbasins, lavatory pans, flushing cisterns etc.- manufacture of resilient floor coverings, such as vinyl, linoleum etc.- manufacture of artificial stone (e.g. cultured marble) ', N'', N'', N'', N'22.2 ', 233)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (237, 398717, 4, N'22.29 ', N'Manufacture of other plastic products ', N'2220 ', N'This class includes:- manufacture of plastic tableware, kitchenware and toilet articles- manufacture of diverse plastic products:  • plastic headgear, insulating fittings, parts of lighting fittings, office or school supplies, articles of apparel (if only sealed together, not sewn), fittings for furniture, statuettes, transmission and conveyer belts, self-adhesive tapes of plastic, plastic shoe lasts, plastic cigar and cigarette holders, combs, plastics hair curlers, plastics novelties, etc. ', N'', N'- Manufacturing of carbon fibre masts- Welding of plastic tanks and pipes ', N'This class excludes:- manufacture of plastic luggage, see 15.12- manufacture of plastic footwear, see 15.20- manufacture of plastic furniture, see 31.01, 31.02, 31.09- manufacture of mattresses of uncovered cellular plastic, see 31.03- manufacture of plastic sports requisites, see 32.30- manufacture of plastic games and toys, see 32.40- manufacture of plastic medical and dental appliances, see 32.50- manufacture of plastic ophthalmic goods, see 32.50- manufacture of plastics hard hats and other personal safety equipment of plastics, see 32.99 ', N'22.2 ', 233)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (238, 398718, 2, N'23 ', N'Manufacture of other non-metallic mineral products ', N'23 ', N'This division includes manufacturing activities related to a single substance of mineral origin. This division includes the manufacture of glass and glass products (e.g. flat glass, hollow glass, fibres, technical glassware etc.), ceramic products, tiles and baked clay products, and cement and plaster, from raw materials to finished articles. ', N'The manufacture of shaped and finished stone and other mineral products is also included in this division. ', N'', N'', N'C ', 88)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (239, 398719, 3, N'23.1 ', N'Manufacture of glass and glass products ', N'231 ', N'This group includes glass in all its forms, made by any process, and articles of glass. ', N'', N'', N'', N'23 ', 238)
