INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (240, 398720, 4, N'23.11 ', N'Manufacture of flat glass ', N'2310 ', N'This class includes:- manufacture of flat glass, including wired, coloured or tinted flat glass ', N'', N'', N'', N'23.1 ', 239)
