INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (241, 398721, 4, N'23.12 ', N'Shaping and processing of flat glass ', N'2310 ', N'This class includes:- manufacture of toughened or laminated flat glass- manufacture of glass mirrors- manufacture of multiple-walled insulating units of glass ', N'', N'', N'', N'23.1 ', 239)
