INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (243, 398723, 4, N'23.14 ', N'Manufacture of glass fibres ', N'2310 ', N'This class includes:- manufacture of glass fibres, including glass wool and non-woven products thereof ', N'', N'', N'This class excludes:- manufacture of woven fabrics of glass yarn, see 13.20- manufacture of fibre optic cable for data transmission or live transmission of images, see 27.31 ', N'23.1 ', 239)
