INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (244, 398724, 4, N'23.19 ', N'Manufacture and processing of other glass, including technical glassware ', N'2310 ', N'This class includes:- manufacture of laboratory, hygienic or pharmaceutical glassware- manufacture of clock or watch glasses, optical glass and optical elements not optically worked- manufacture of glassware used in imitation jewellery- manufacture of glass insulators and glass insulating fittings- manufacture of glass envelopes for lamps- manufacture of glass figurines- manufacture of glass paving blocks- manufacture of glass in rods or tubes ', N'', N'', N'This class excludes:- manufacture of optical elements optically worked, see 26.70- manufacture of syringes and other medical laboratory equipment, see 32.50 ', N'23.1 ', 239)
