INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (269, 398749, 4, N'23.91 ', N'Production of abrasive products ', N'2399 ', N'This class includes:- manufacture of millstones, sharpening or polishing stones and natural or artificial abrasive products on a support, including abrasive products on a soft base (e.g. sandpaper) ', N'', N'', N'', N'23.9 ', 268)
