INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (333, 398813, 4, N'26.60 ', N'Manufacture of irradiation, electromedical and electrotherapeutic equipment ', N'2660 ', N'This class includes:- manufacture of irradiation apparatus and tubes (e.g. industrial, medical diagnostic, medical therapeutic, research, scientific):  • beta-, gamma, X-ray or other radiation equipment- manufacture of CT scanners- manufacture of PET scanners- manufacture of magnetic resonance imaging (MRI) equipment- manufacture of medical ultrasound equipment- manufacture of electrocardiographs- manufacture of electromedical endoscopic equipment- manufacture of medical laser equipment- manufacture of pacemakers- manufacture of hearing aids ', N'This class also includes:- manufacture of food and milk irradiation equipment ', N'', N'This class excludes:- manufacture of tanning beds, see 27.90 ', N'26.6 ', 332)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (334, 398814, 3, N'26.7 ', N'Manufacture of optical instruments and photographic equipment ', N'267 ', N'', N'', N'', N'', N'26 ', 319)
