INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (337, 398817, 4, N'26.80 ', N'Manufacture of magnetic and optical media ', N'2680 ', N'This class includes the manufacture of magnetic and optical recording media.This class includes:- manufacture of blank magnetic audio and video tapes- manufacture of blank magnetic audio and video cassettes- manufacture of blank diskettes- manufacture of blank optical discs- manufacture of hard drive media ', N'', N'', N'This class excludes:- reproduction of recorded media (computer media, sound, video, etc.), see 18.2 ', N'26.8 ', 336)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (338, 398818, 2, N'27 ', N'Manufacture of electrical equipment ', N'27 ', N'This division includes the manufacture of products that generate, distribute and use electrical power. ', N'Also included is the manufacture of electrical lighting, signalling equipment and electric household appliances. ', N'', N'This division excludes the manufacture of electronic products (see division 26). ', N'C ', 88)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (339, 398819, 3, N'27.1 ', N'Manufacture of electric motors, generators, transformers and electricity distribution and control apparatus ', N'271 ', N'This group comprises the manufacture of power, distribution and specialty transformers; electric motors, generators, and motor generator sets. ', N'', N'', N'', N'27 ', 338)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (340, 398820, 4, N'27.11 ', N'Manufacture of electric motors, generators and transformers ', N'2710 ', N'This class includes manufacture of all electric motors and transformers: AC, DC and AC/DC.This class includes:- manufacture of electric motors (except internal combustion engine starting motors)- manufacture of distribution transformers, electric- manufacture of arc-welding transformers- manufacture of fluorescent ballasts (i.e. transformers)- manufacture of substation transformers for electric power distribution- manufacture of transmission and distribution voltage regulators- manufacture of power generators (except battery charging alternators for internal combustion engines)- manufacture of motor generator sets (except turbine generator set units)- rewinding of armatures on a factory basis ', N'', N'- Manufacture of photovoltaic solar generators ', N'This class excludes:- manufacture of electronic component-type transformers and switches, see 26.11- manufacture of electric welding and soldering equipment, see 27.90- manufacture of solid state inverters, rectifiers and converters, see 27.90- manufacture of turbine-generator sets, see 28.11- manufacture of starting motors and generators for internal combustion engines, see 29.31 ', N'27.1 ', 339)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (341, 398821, 4, N'27.12 ', N'Manufacture of electricity distribution and control apparatus ', N'2710 ', N'This class includes:- manufacture of power circuit breakers- manufacture of surge suppressors (for distribution level voltage)- manufacture of control panels for electric power distribution- manufacture of electrical relays- manufacture of duct for electrical switchboard apparatus- manufacture of electric fuses- manufacture of power switching equipment- manufacture of electric power switches (except pushbutton, snap, solenoid, tumbler)- manufacture of prime mover generator sets ', N'', N'', N'This class excludes:- manufacture of environmental controls and industrial process control instruments, see 26.51- manufacture of switches for electrical circuits, such as pushbutton and snap switches, see 27.33 ', N'27.1 ', 339)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (342, 398822, 3, N'27.2 ', N'Manufacture of batteries and accumulators ', N'272 ', N'', N'', N'', N'', N'27 ', 338)
