INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (343, 398823, 4, N'27.20 ', N'Manufacture of batteries and accumulators ', N'2720 ', N'This class includes the manufacture of non-rechargeable and rechargeable batteries.This class includes:- manufacture of primary cells and primary batteries   • cells containing manganese dioxide, mercuric dioxide, silver oxide etc.- manufacture of electric accumulators, including parts thereof:  • separators, containers, covers- manufacture of lead acid batteries- manufacture of NiCad batteries- manufacture of NiMH batteries- manufacture of lithium batteries- manufacture of dry cell batteries- manufacture of wet cell batteries ', N'', N'', N'', N'27.2 ', 342)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (344, 398824, 3, N'27.3 ', N'Manufacture of wiring and wiring devices ', N'273 ', N'This group includes the manufacture of current-carrying wiring devices and non current-carrying wiring devices for wiring electrical circuits regardless of material. ', N'This group also includes the insulating of wire and the manufacture of fibre optic cables. ', N'', N'', N'27 ', 338)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (345, 398825, 4, N'27.31 ', N'Manufacture of fibre optic cables ', N'2731 ', N'This class includes:- manufacture of fibre optic cable for data transmission or live transmission of images ', N'', N'', N'This class excludes:- manufacture of glass fibres or strand, see 23.14- manufacture of optical cable sets or assemblies with connectors or other attachments, see depending on application, e.g. 26.11 ', N'27.3 ', 344)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (346, 398826, 4, N'27.32 ', N'Manufacture of other electronic and electric wires and cables ', N'2732 ', N'This class includes:- manufacture of insulated wire and cable, made of steel, copper, aluminium ', N'', N'', N'This class excludes:- manufacture (drawing) of wire, see 24.34, 24.41, 24.42, 24.43, 24.44, 24.45- manufacture of computer cables, printer cables, USB cables and similar cable sets or assemblies, see 26.11- manufacture of electrical cord sets with insulated wire and connectors, see 27.90- manufacture of cable sets, wiring harnesses and similar cable sets or assemblies for automotive applications, see 29.31 ', N'27.3 ', 344)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (347, 398827, 4, N'27.33 ', N'Manufacture of wiring devices ', N'2733 ', N'This class includes the manufacture of current-carrying and non current-carrying wiring devices for electrical circuits regardless of material.This class includes:- manufacture of bus bars, electrical conductors (except switchgear-type) - manufacture of GFCI (ground fault circuit interrupters) - manufacture of lamp holders - manufacture of lightning arrestors and coils- manufacture of switches for electrical wiring (e.g. pressure, pushbutton, snap, tumbler switches) - manufacture of electrical outlets or sockets- manufacture of boxes for electrical wiring (e.g. junction, outlet, switch boxes) - manufacture of electrical conduit and fitting - manufacture of transmission pole and line hardware - manufacture of plastic non current carrying wiring devices including plastic junction boxes, face plates and similar, plastic pole line fittings and switch covers ', N'', N'', N'This class excludes:- manufacture of ceramic insulators, see 23.43- manufacture of electronic component-type connectors, sockets, and switches, see 26.11 ', N'27.3 ', 344)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (348, 398828, 3, N'27.4 ', N'Manufacture of electric lighting equipment ', N'274 ', N'', N'', N'', N'', N'27 ', 338)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (349, 398829, 4, N'27.40 ', N'Manufacture of electric lighting equipment ', N'2740 ', N'This class includes the manufacture of electric light bulbs and tubes and parts and components thereof (except glass blanks for electric light bulbs), electric lighting fixtures and lighting fixture components (except current-carrying wiring devices).This class includes:- manufacture of discharge, incandescent, fluorescent, ultra-violet, infra-red etc. lamps, fixtures and bulbs- manufacture of ceiling lighting fixtures- manufacture of chandeliers- manufacture of table lamps (i.e. lighting fixture)- manufacture of Christmas tree lighting sets - manufacture of electric fireplace logs - manufacture of flashlights- manufacture of electric insect lamps- manufacture of lanterns (e.g. carbide, electric, gas, gasoline, kerosene)- manufacture of spotlights- manufacture of street lighting fixtures (except traffic signals)- manufacture of lighting equipment for transportation equipment (e.g. for motor vehicles, aircraft, boats) ', N'This class also includes:- manufacture of non-electrical lighting equipment ', N'', N'This class excludes:- manufacture of glassware and glass parts for lighting fixtures, see 23.19- manufacture of current-carrying wiring devices for lighting fixtures, see 27.33- manufacture of ceiling fans or bath fans with integrated lighting fixtures, see 27.51- manufacture of electrical signalling equipment such as traffic lights and pedestrian signalling equipment, see 27.90- manufacture of electrical signs, see 27.90 ', N'27.4 ', 348)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (350, 398830, 3, N'27.5 ', N'Manufacture of domestic appliances ', N'275 ', N'This group includes the manufacture of small electric appliances and electric housewares, household-type fans, household-type vacuum cleaners, electric household-type floor care machines, household-type cooking appliances, household-type laundry equipment, household-type refrigerators, upright and chest freezers, and other electrical and non-electrical household appliances, such as dishwashers, water heaters, and garbage disposal units. This group includes the manufacture of appliances with electric, gas or other fuel sources. ', N'', N'', N'', N'27 ', 338)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (351, 398831, 4, N'27.51 ', N'Manufacture of electric domestic appliances ', N'2750 ', N'This class includes:- manufacture of domestic electric appliances:  • refrigerators   • freezers  • dishwashers  • washing and drying machines  • vacuum cleaners  • floor polishers  • waste disposers  • grinders, blenders, juice squeezers  • tin openers  • electric shavers, electric toothbrushes, and other electric personal care device  • knife sharpeners  • ventilating or recycling hoods- manufacture of domestic electrothermic appliances:  • electric water heaters  • electric blankets  • electric dryers, combs, brushes, curlers  • electric smoothing irons  • space heaters and household-type fans, portable  • electric ovens  • microwave ovens  • cookers, hotplates  • toasters  • coffee or tea makers  • fry pans, roasters, grills, hoods  • electric heating resistors etc. ', N'', N'', N'This class excludes:- manufacture of commercial and industrial refrigerators and freezers, room air-conditioners, attic fans, permanent mount space heaters, and commercial ventilation and exhaust fans, commercial-type cooking equipment; commercial-type laundry, dry-cleaning, and pressing equipment; commercial, industrial, and institutional vacuum cleaners, see division 28- manufacture of household-type sewing machines, see 28.94- installation of central vacuum cleaning systems, 43.29 ', N'27.5 ', 350)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (352, 398832, 4, N'27.52 ', N'Manufacture of non-electric domestic appliances ', N'2750 ', N'This class includes:- manufacture of domestic non-electric cooking and heating equipment:  • non-electric space heaters, cooking ranges, grates, stoves, water heaters, cooking appliances, plate warmers ', N'', N'', N'', N'27.5 ', 350)
GO
