INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (414, 398894, 4, N'32.13 ', N'Manufacture of imitation jewellery and related articles ', N'3212 ', N'This class includes:- manufacture of costume or imitation jewellery:  • rings, bracelets, necklaces, and similar articles of jewellery made from base metals plated with precious metals  • jewellery containing imitation stones such as imitation gems stones, imitation diamonds, and similar- manufacture of metal watch bands (except precious metal) ', N'', N'', N'This class excludes:- manufacture of jewellery made from precious metals or clad with precious metals, see 32.12- manufacture of jewellery containing genuine gem stones, see 32.12- manufacture of precious metal watch bands, see 32.12 ', N'32.1 ', 411)
GO
