INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (420, 398900, 4, N'32.40 ', N'Manufacture of games and toys ', N'3240 ', N'This class includes the manufacture of dolls, toys and games (including electronic games), scale models and children''s vehicles (except metal bicycles and tricycles).This class includes:- manufacture of dolls and doll garments, parts and accessories- manufacture of action figures- manufacture of toy animals- manufacture of toy musical instruments- manufacture of playing cards- manufacture of board games and similar games- manufacture of electronic games: chess etc.- manufacture of reduced-size ("scale") models and similar recreational models, electrical trains, construction sets etc.- manufacture of coin-operated games, billiards, special tables for casino games, etc.- manufacture of articles for funfair, table or parlour games- manufacture of wheeled toys designed to be ridden, including plastic bicycles and tricycles- manufacture of puzzles and similar articles ', N'', N'- Manufacture of toys made of fabric ', N'This class excludes:- manufacture of video game consoles, see 26.40- manufacture of bicycles, see 30.92- manufacture of articles for jokes and novelties, see 32.99- writing and publishing of software for video game consoles, see 58.21, 62.01 ', N'32.4 ', 419)
