INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (424, 398904, 4, N'32.91 ', N'Manufacture of brooms and brushes ', N'3290 ', N'This class includes:- manufacture of brooms and brushes, including brushes constituting parts of machines, hand-operated mechanical floor sweepers, mops and feather dusters, paint brushes, paint pads and rollers, squeegees and other brushes, brooms, mops etc.- manufacture of shoe and clothes brushes ', N'', N'', N'', N'32.9 ', 423)
