INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (437, 398917, 4, N'33.20 ', N'Installation of industrial machinery and equipment ', N'3320 ', N'This class includes the specialised installation of machinery. However, the installation of equipment that forms an integral part of buildings or similar structures, such as installation of escalators, electrical wiring, burglar alarm systems or air-conditioning systems, is classified as construction.This class includes:- installation of industrial machinery in industrial plant- installation and assembly of industrial process control equipment- installation of other industrial equipment, e.g.:  • communications equipment  • mainframe and similar computers  • irradiation and electromedical equipment etc.- dismantling large-scale machinery and equipment- activities of millwrights- machine rigging- installation of bowling alley equipment ', N'', N'', N'This class excludes:- installation of elevators, escalators, automated doors, vacuum cleaning systems etc., see 43.29- installation of doors, staircases, shop fittings, furniture etc., see 43.32- installation (setting-up) of personal computers, see 62.09 ', N'33.2 ', 436)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (438, 398918, 1, N'D ', N'ELECTRICITY, GAS, STEAM AND AIR CONDITIONING SUPPLY ', N'D ', N'This section includes the activity of providing electric power, natural gas, steam, hot water and the like through a permanent infrastructure (network) of lines, mains and pipes. The dimension of the network is not decisive; also included are the distribution of electricity, gas, steam, hot water and the like in industrial parks or residential buildings.This section therefore includes the operation of electric and gas utilities, which generate, control and distribute electric power or gas. ', N'Also included is the provision of steam and air-conditioning supply. ', N'', N'This section excludes the operation of water and sewerage utilities, see 36, 37. This section also excludes the (typically long-distance) transport of gas through pipelines. ', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (439, 398919, 2, N'35 ', N'Electricity, gas, steam and air conditioning supply ', N'35 ', N'', N'', N'', N'', N'D ', 438)
