INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (440, 398920, 3, N'35.1 ', N'Electric power generation, transmission and distribution ', N'351 ', N'This group includes the generation of bulk electric power, transmission from generating facilities to distribution centres and distribution to end users. ', N'', N'', N'', N'35 ', 439)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (441, 398921, 4, N'35.11 ', N'Production of electricity ', N'3510 ', N'This class includes:- operation of generation facilities that produce electric energy; including thermal, nuclear, hydroelectric, gas turbine, diesel and renewable ', N'', N'', N'This class excludes:- production of electricity through incineration of waste, see 38.21 ', N'35.1 ', 440)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (442, 398922, 4, N'35.12 ', N'Transmission of electricity ', N'3510 ', N'This class includes:- operation of transmission systems that convey the electricity from the generation facility to the distribution system ', N'', N'', N'', N'35.1 ', 440)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (443, 398923, 4, N'35.13 ', N'Distribution of electricity ', N'3510 ', N'This class includes:- operation of distribution systems (i.e., consisting of lines, poles, meters, and wiring) that convey electric power received from the generation facility or the transmission system to the final consumer ', N'', N'', N'', N'35.1 ', 440)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (444, 398924, 4, N'35.14 ', N'Trade of electricity ', N'3510 ', N'This class includes:- sale of electricity to the user- activities of electric power brokers or agents that arrange the sale of electricity via power distribution systems operated by others- operation of electricity and transmission capacity exchanges for electric power ', N'', N'- Charging stations for mobile phones and laptops ', N'', N'35.1 ', 440)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (445, 398925, 3, N'35.2 ', N'Manufacture of gas; distribution of gaseous fuels through mains ', N'352 ', N'This group includes the manufacture of gas and the distribution of natural or synthetic gas to the consumer through a system of mains. Gas marketers or brokers, which arrange the sale of natural gas over distribution systems operated by others, are included. The separate operation of gas pipelines, typically done over long distances, connecting producers with distributors of gas, or between urban centres, is excluded from this group and classified with other pipeline transport activities. ', N'', N'', N'', N'35 ', 439)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (446, 398926, 4, N'35.21 ', N'Manufacture of gas ', N'3520 ', N'This class includes:- production of gas for the purpose of gas supply by carbonation of coal, from by-products of agriculture or from waste- manufacture of gaseous fuels with a specified calorific value, by purification, blending and other processes from gases of various types including natural gas ', N'', N'- Gasification of lignite- Transformation of pipeline natural gas into compressed natural gas (CNG) off mine site ', N'This class excludes:- production of crude natural gas, see 06.20- operation of coke ovens, see 19.10- manufacture of refined petroleum products, see 19.20- manufacture of industrial gases, see 20.11 ', N'35.2 ', 445)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (447, 398927, 4, N'35.22 ', N'Distribution of gaseous fuels through mains ', N'3520 ', N'This class includes:- distribution and supply of gaseous fuels of all kinds through a system of mains ', N'', N'', N'This class excludes:- (long-distance) transportation of gases by pipelines, see 49.50 ', N'35.2 ', 445)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (448, 398928, 4, N'35.23 ', N'Trade of gas through mains ', N'3520 ', N'This class includes:- sale of gas to the user through mains- activities of gas brokers or agents that arrange the sale of gas over gas distribution systems operated by others- commodity and transport capacity exchanges for gaseous fuels ', N'', N'', N'This class excludes:- wholesale of gaseous fuels, see 46.71- retail sale of bottled gas, see 47.78- direct selling of fuel, see 47.99 ', N'35.2 ', 445)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (449, 398929, 3, N'35.3 ', N'Steam and air conditioning supply ', N'353 ', N'', N'', N'', N'', N'35 ', 439)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (450, 398930, 4, N'35.30 ', N'Steam and air conditioning supply ', N'3530 ', N'This class includes:- production, collection and distribution of steam and hot water for heating, power and other purposes- production and distribution of cooled air- production and distribution of chilled water for cooling purposes- production of ice, for food and non-food (e.g. cooling) purposes ', N'', N'', N'', N'35.3 ', 449)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (451, 398931, 1, N'E ', N'WATER SUPPLY; SEWERAGE, WASTE MANAGEMENT AND REMEDIATION ACTIVITIES ', N'E ', N'This section includes activities related to the management (including collection, treatment and disposal) of various forms of waste, such as solid or non-solid industrial or household waste, as well as contaminated sites. The output of the waste or sewage treatment process can either be disposed of or become an input into other production processes. ', N'Activities of water supply are also grouped in this section, since they are often carried out in connection with, or by units also engaged in, the treatment of sewage. ', N'', N'', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (452, 398932, 2, N'36 ', N'Water collection, treatment and supply ', N'36 ', N'This division includes the collection, treatment and distribution of water for domestic and industrial needs. Collection of water from various sources, as well as distribution by various means is included. ', N'', N'', N'', N'E ', 451)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (453, 398933, 3, N'36.0 ', N'Water collection, treatment and supply ', N'360 ', N'', N'', N'', N'', N'36 ', 452)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (454, 398934, 4, N'36.00 ', N'Water collection, treatment and supply ', N'3600 ', N'This class includes water collection, treatment and distribution activities for domestic and industrial needs. Collection of water from various sources, as well as distribution by various means is included.This class includes:- collection of water from rivers, lakes, wells etc.- collection of rain water- purification of water for water supply purposes- treatment of water for industrial and other purposes- desalting of sea or ground water to produce water as the principal product of interest- distribution of water through mains, by trucks or other means- operation of irrigation canals ', N'The operation of irrigation canals is also included; however the provision of irrigation services through sprinklers, and similar agricultural support services, is not included. ', N'', N'This class excludes:- operation of irrigation equipment for agricultural purposes, see 01.61- treatment of wastewater in order to prevent pollution, see 37.00- (long-distance) transport of water via pipelines, see 49.50 ', N'36.0 ', 453)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (455, 398935, 2, N'37 ', N'Sewerage ', N'37 ', N'This division includes the operation of sewer systems or sewage treatment facilities that collect, treat, and dispose of sewage. ', N'', N'', N'', N'E ', 451)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (456, 398936, 3, N'37.0 ', N'Sewerage ', N'370 ', N'', N'', N'', N'', N'37 ', 455)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (457, 398937, 4, N'37.00 ', N'Sewerage ', N'3700 ', N'This class includes:- operation of sewer systems or sewer treatment facilities- collecting and transporting of human or industrial wastewater from one or several users, as well as rain water by means of sewerage networks, collectors, tanks and other means of transport (sewage vehicles etc.) - emptying and cleaning of cesspools and septic tanks, sinks and pits from sewage; servicing of chemical toilets- treatment of wastewater (including human and industrial wastewater, water from swimming pools etc.) by means of physical, chemical and biological processes like dilution, screening, filtering, sedimentation etc.- maintenance and cleaning of sewers and drains, including sewer rodding ', N'', N'', N'This class excludes:- decontamination of surface water and groundwater at the place of pollution, see 39.00- cleaning and deblocking of drainpipes in buildings, see 43.22 ', N'37.0 ', 456)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (458, 398938, 2, N'38 ', N'Waste collection, treatment and disposal activities; materials recovery ', N'38 ', N'This division includes the collection, treatment, and disposal of waste materials. ', N'This also includes local hauling of waste materials and the operation of materials recovery facilities (i.e. those that sort recoverable materials from a waste stream). ', N'', N'', N'E ', 451)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (459, 398939, 3, N'38.1 ', N'Waste collection ', N'381 ', N'This group includes the collection of waste from households and businesses by means of refuse bins, wheeled bins, containers, etc. It includes collection of non-hazardous and hazardous waste e.g. waste from households, used batteries, used cooking oils and fats, waste oil from ships and used oil from garages, as well as construction and demolition waste. ', N'', N'', N'', N'38 ', 458)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (460, 398940, 4, N'38.11 ', N'Collection of non-hazardous waste ', N'3811 ', N'This class includes:- collection of non-hazardous solid waste (i.e. garbage) within a local area, such as collection of waste from households and businesses by means of refuse bins, wheeled bins, containers etc may include mixed recoverable materials- collection of recyclable materials- collection of refuse in litter-bins in public places ', N'This class also includes:- collection of construction and demolition waste- collection and removal of debris such as brush and rubble- collection of waste output of textile mills- operation of waste transfer facilities for non-hazardous waste ', N'- Activities of recycling centres for non-hazardous waste ', N'This class excludes:- collection of hazardous waste, see 38.12- operation of landfills for the disposal of non-hazardous waste, see 38.21- operation of facilities where commingled recoverable materials such as paper, plastics, etc. are sorted into distinct categories, see 38.32 ', N'38.1 ', 459)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (461, 398941, 4, N'38.12 ', N'Collection of hazardous waste ', N'3812 ', N'This class includes the collection of solid and non-solid hazardous waste, i.e. explosive, oxidizing, flammable, toxic, irritant, carcinogenic, corrosive, infectious and other substances and preparations harmful for human health and environment. It may also entail identification, treatment, packaging and labeling of waste for the purposes of transport.This class includes:- collection of hazardous waste, such as:  • used oil from shipment or garages  • bio-hazardous waste  • nuclear waste  • used batteries etc.- operation of waste transfer stations for hazardous waste ', N'', N'', N'This class excludes:- remediation and clean up of contaminated buildings, mine sites, soil, ground water, e.g. asbestos removal, see 39.00 ', N'38.1 ', 459)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (462, 398942, 3, N'38.2 ', N'Waste treatment and disposal ', N'382 ', N'This group includes the disposal and treatment prior to disposal of various forms of waste by different means, such as treatment of organic waste with the aim of disposal; treatment and disposal of toxic live or dead animals and other contaminated waste; treatment and disposal of transition radioactive waste from hospitals, etc.; dumping of refuse on land or in water; burial or ploughing-under of refuse; disposal of used goods such as refrigerators to eliminate harmful waste; disposal of waste by incineration or combustion. Included is also energy recovery resulting from waste incineration process. ', N'', N'', N'This group excludes:- treatment and disposal of wastewater, see 37.00- materials recovery, see 38.3 ', N'38 ', 458)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (463, 398943, 4, N'38.21 ', N'Treatment and disposal of non-hazardous waste ', N'3821 ', N'This class includes the disposal and treatment prior to disposal of solid or non-solid non-hazardous waste:- operation of landfills for the disposal of non-hazardous waste- disposal of non-hazardous waste by combustion or incineration or other methods, with or without the resulting production of electricity or steam, compost, substitute fuels, biogas, ashes or other by-products for further use etc.- treatment of organic waste for disposal ', N'', N'', N'This class excludes:- incineration and combustion of hazardous waste, see 38.22- operation of facilities where commingled recoverable materials such as paper, plastics, used beverage cans and metals, are sorted into distinct categories, see 38.32- decontamination, clean up of land, water; toxic material abatement, see 39.00 ', N'38.2 ', 462)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (464, 398944, 4, N'38.22 ', N'Treatment and disposal of hazardous waste ', N'3822 ', N'This class includes the disposal and treatment prior to disposal of solid or non-solid hazardous waste, including waste that if explosive, oxidising, flammable, toxic, irritant, carcinogenic, corrosive, infectious and other substances and preparations harmful for human health and environment.This class includes:- operation of facilities for treatment of hazardous waste- treatment and disposal of toxic live or dead animals and other contaminated waste- incineration of hazardous waste- disposal of used goods such as refrigerators to eliminate harmful waste- treatment, disposal and storage of radioactive nuclear waste including:   • treatment and disposal of transition radioactive waste, i.e. decaying within the period of transport, from hospitals  • encapsulation, preparation and other treatment of nuclear waste for storage ', N'', N'', N'This class excludes:- reprocessing of nuclear fuels, see 20.13- incineration of non-hazardous waste, see 38.21- decontamination, clean up of land, water; toxic material abatement, see 39.00 ', N'38.2 ', 462)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (465, 398945, 3, N'38.3 ', N'Materials recovery ', N'383 ', N'', N'', N'', N'', N'38 ', 458)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (466, 398946, 4, N'38.31 ', N'Dismantling of wrecks ', N'3830 ', N'This class includes dismantling of wrecks of any type (automobiles, ships, computers, televisions and other equipment) for materials recovery. ', N'', N'', N'This class excludes:- disposal of used goods such as refrigerators to eliminate harmful waste, see 38.22- dismantling of automobiles, ships, computers, televisions and other equipment to obtain re-sell usable parts, see section G ', N'38.3 ', 465)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (467, 398947, 4, N'38.32 ', N'Recovery of sorted materials ', N'3830 ', N'This class includes the processing of metal and non-metal waste and scrap and other articles into secondary raw materials, usually involving a mechanical or chemical transformation process.Examples of the mechanical or chemical transformation processes that are undertaken are:- mechanical crushing of metal waste from used cars, washing machines, bikes etc.- mechanical reduction of large iron pieces such as railway wagons- shredding of metal waste, end-of-life vehicles etc.- other methods of mechanical treatment as cutting, pressing to reduce the volume- reclaiming metals out of photographic waste, e.g. fixer solution or photographic films and paper - reclaiming of rubber such as used tyres to produce secondary raw material- sorting and pelleting of plastics to produce secondary raw material for tubes, flower pots, pallets and the like- processing (cleaning, melting, grinding) of plastic or rubber waste to granulates- crushing, cleaning and sorting of glass- crushing, cleaning and sorting of other waste such as demolition waste to obtain secondary raw material- processing of used cooking oils and fats into secondary raw materials- processing of other food, beverage and tobacco waste and residual substances into secondary raw materials ', N'Also included is the recovery of materials from waste streams in the form of (1) separating and sorting recoverable materials from non-hazardous waste streams (i.e. garbage) or (2) the separating and sorting of commingled recoverable materials, such as paper, plastics, used beverage cans and metals, into distinct categories. ', N'- Rubber powder from rubber waste and scrap, including decontamination, division (elimination) of fractions and devulcanising- Extraction of silver from waste chemicals, other than by electrolytic refining- Treatment and disposal, as well as recovery, of waste oil- Production of iron from clinker ', N'This class excludes:- manufacture of new final products from (whether or not self-manufactured) secondary raw materials, such as spinning yarn from garnetted stock, making pulp from paper waste, retreading tyres or production of metal from metal scrap, see corresponding classes in section C (Manufacturing)- reprocessing of nuclear fuels, see 20.13- remelting ferrous waste and scrap, see 24.10- materials recovery during waste combustion or incineration process, see 38.2- treatment and disposal of non-hazardous waste, see 38.21- treatment of organic waste for disposal, including production of compost, see 38.21- energy recovery during non-hazardous waste incineration process, see 38.21- treatment and disposal of transition radioactive waste from hospitals etc., see 38.22- treatment and disposal of toxic, contaminated waste, see 38.22- wholesale of recoverable materials, see 46.77 ', N'38.3 ', 465)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (468, 398948, 2, N'39 ', N'Remediation activities and other waste management services ', N'39 ', N'This division includes the provision of remediation services, i.e. the cleanup of contaminated buildings and sites, soil, surface or ground water. ', N'', N'', N'', N'E ', 451)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (469, 398949, 3, N'39.0 ', N'Remediation activities and other waste management services ', N'390 ', N'', N'', N'', N'', N'39 ', 468)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (470, 398950, 4, N'39.00 ', N'Remediation activities and other waste management services ', N'3900 ', N'This class includes:- decontamination of soils and groundwater at the place of pollution, either in situ or ex situ, using e.g. mechanical, chemical or biological methods- decontamination of industrial plants or sites, including nuclear plants and sites- decontamination and cleaning up of surface water following accidental pollution, e.g. through collection of pollutants or through application of chemicals- cleaning up oil spills and other pollutions on land, in surface water, in ocean and seas, including coastal areas- asbestos, lead paint, and other toxic material abatement- clearing of landmines and the like (including detonation)- other specialised pollution-control activities ', N'', N'', N'This class excludes:- pest control in agriculture, see 01.61- purification of water for water supply purposes, see 36.00- treatment and disposal of non-hazardous waste, see 38.21- treatment and disposal of hazardous waste, see 38.22- outdoor sweeping and watering of streets etc., see 81.29 ', N'39.0 ', 469)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (471, 398951, 1, N'F ', N'CONSTRUCTION ', N'F ', N'This section includes general construction and specialised construction activities for buildings and civil engineering works. It includes new work, repair, additions and alterations, the erection of prefabricated buildings or structures on the site and also construction of a temporary nature. General construction is the construction of entire dwellings, office buildings, stores and other public and utility buildings, farm buildings etc., or the construction of civil engineering works such as motorways, streets, bridges, tunnels, railways, airfields, harbours and other water projects, irrigation systems, sewerage systems, industrial facilities, pipelines and electric lines, sports facilities etc. This work can be carried out on own account or on a fee or contract basis. Portions of the work and sometimes even the whole practical work can be subcontracted out. A unit that carries the overall responsibility for a construction project is classified here.Also included is the repair of buildings and engineering works.This section includes the complete construction of buildings (division 41), the complete construction of civil engineering works (division 42), as well as specialised construction activities, if carried out only as a part of the construction process (division 43).The rental of construction equipment with operator is classified with the specific construction activity carried out with this equipment. ', N'This section also includes the development of building projects for buildings or civil engineering works by bringing together financial, technical and physical means to realise the construction projects for later sale. ', N'', N'If these activities are carried out not for later sale of the construction projects, but for their operation (e.g. rental of space in these buildings, manufacturing activities in these plants), the unit would not be classified here, but according to its operational activity, i.e. real estate, manufacturing etc. ', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (472, 398952, 2, N'41 ', N'Construction of buildings ', N'41 ', N'This division includes general construction of buildings of all kinds. It includes new work, repair, additions and alterations, the erection of pre-fabricated buildings or structures on the site and also construction of temporary nature. Included is the construction of entire dwellings, office buildings, stores and other public and utility buildings, farm buildings, etc. ', N'', N'', N'', N'F ', 471)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (473, 398953, 3, N'41.1 ', N'Development of building projects ', N'410 ', N'', N'', N'', N'', N'41 ', 472)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (474, 398954, 4, N'41.10 ', N'Development of building projects ', N'4100 ', N'This class includes:- development of building projects for residential and non-residential buildings by bringing together financial, technical and physical means to realise the building projects for later sale ', N'', N'- Building co-operatives, terminating their activity when the construction of the buildings is finalised ', N'This class excludes:- construction of buildings, see 41.20- architectural and engineering activities, see 71.1- project management services related to building projects, see 71.1 ', N'41.1 ', 473)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (475, 398955, 3, N'41.2 ', N'Construction of residential and non-residential buildings ', N'410 ', N'This group includes the construction of complete residential or non-residential buildings, on own account for sale or on a fee or contract basis. Outsourcing parts or even the whole construction process is possible. If only specialised parts of the construction process are carried out, the activity is classified in division 43. ', N'', N'', N'', N'41 ', 472)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (476, 398956, 4, N'41.20 ', N'Construction of residential and non-residential buildings ', N'4100 ', N'This class includes:- construction of all types of residential buildings:  • single-family houses  • multi-family buildings, including high-rise buildings- construction of all types of non-residential buildings:  • buildings for industrial production, e.g. factories, workshops, assembly plants etc.  • hospitals, schools, office buildings  • hotels, stores, shopping malls, restaurants  • airport buildings  • indoor sports facilities  • parking garages, including underground parking garages  • warehouses  • religious buildings- assembly and erection of prefabricated constructions on the site ', N'This class also includes:- remodelling or renovating existing residential structures ', N'- Assembly of silos- Building and installation of grain storage systems ', N'This class excludes:- construction of industrial facilities, except buildings, see 42.99- architectural and engineering activities, see 71.1- project management for construction, see 71.1 ', N'41.2 ', 475)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (477, 398957, 2, N'42 ', N'Civil engineering ', N'42 ', N'This division includes general construction for civil engineering objects. It includes new work, repair, additions and alterations, the erection of pre-fabricated structures on the site and also construction of temporary nature. Included is the construction of heavy constructions such as motorways, streets, bridges, tunnels, railways, airfields, harbours and other water projects, irrigation systems, sewerage systems, industrial facilities, pipelines and electric lines, outdoor sports facilities, etc. This work can be carried out on own account or on a fee or contract basis. Portions of the work and sometimes even the whole practical work can be subcontracted out. ', N'', N'', N'', N'F ', 471)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (478, 398958, 3, N'42.1 ', N'Construction of roads and railways ', N'421 ', N'', N'', N'', N'', N'42 ', 477)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (479, 398959, 4, N'42.11 ', N'Construction of roads and motorways ', N'4210 ', N'This class includes:- construction of motorways, streets, roads, other vehicular and pedestrian ways- surface work on streets, roads, highways, bridges or tunnels:  • asphalt paving of roads  • road painting and other marking  • installation of crash barriers, traffic signs and the like- construction of airfield runways ', N'', N'', N'This class excludes:- installation of street lighting and electrical signals, see 43.21- architectural and engineering activities, see 71.1- project management for construction, see 71.1 ', N'42.1 ', 478)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (480, 398960, 4, N'42.12 ', N'Construction of railways and underground railways ', N'4210 ', N'This class includes:- construction of railways and subways ', N'', N'', N'This class excludes:- installation of lighting and electrical signals, see 43.21- architectural and engineering activities, see 71.1- project management for construction, see 71.1 ', N'42.1 ', 478)
GO
