INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (505, 398985, 4, N'43.99 ', N'Other specialised construction activities n.e.c. ', N'4390 ', N'This class includes:- construction activities specialising in one aspect common to different kind of structures, requiring specialised skill or equipment:  • construction of foundations, including pile driving  • damp proofing and water proofing works  • de-humidification of buildings  • shaft sinking  • erection of steel elements  • steel bending  • bricklaying and stone setting  • scaffolds and work platform erecting and dismantling, excluding rental of scaffolds and work platforms  • erection of chimneys and industrial ovens  • work with specialist access requirements necessitating climbing skills and the use of related equipment, e.g. working at height on tall structures- subsurface work- construction of outdoor swimming pools- steam cleaning, sand blasting and similar activities for building exteriors- rental of cranes and other building equipment, which cannot be allocated to a specific construction type, with operator ', N'', N'', N'This class excludes:- rental of construction machinery and equipment without operator, see 77.32 ', N'43.9 ', 503)
