INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (622, 399102, 1, N'H ', N'TRANSPORTATION AND STORAGE ', N'H ', N'This section includes the provision of passenger or freight transport, whether scheduled or not, by rail, pipeline, road, water or air and associated activities such as terminal and parking facilities, cargo handling, storage etc. Included in this section is the rental of transport equipment with driver or operator. Also included are postal and courier activities. ', N'', N'', N'This section excludes:- major repair or alteration of transport equipment, except motor vehicles, see group 33.1- construction, maintenance and repair of roads, railways, harbours, airfields, see division 42- maintenance and repair of motor vehicles, see 45.20- rental of transport equipment without driver or operator, see 77.1, 77.3 ', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (623, 399103, 2, N'49 ', N'Land transport and transport via pipelines ', N'49 ', N'This division includes the transport of passengers and freight via road and rail, as well as freight transport via pipelines. ', N'', N'', N'', N'H ', 622)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (624, 399104, 3, N'49.1 ', N'Passenger rail transport, interurban ', N'491 ', N'', N'', N'', N'', N'49 ', 623)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (625, 399105, 4, N'49.10 ', N'Passenger rail transport, interurban ', N'4911 ', N'This class includes:- rail transportation of passengers using railroad rolling stock on mainline networks, spread over an extensive geographic area- passenger transport by interurban railways - operation of sleeping cars or dining cars as an integrated operation of railway companies ', N'', N'', N'This class excludes:- passenger transport by urban and suburban transit systems, see 49.31- passenger terminal activities, see 52.21- operation of railroad infrastructure; related activities such as switching and shunting, see 52.21- operation of sleeping cars or dining cars when operated by separate units, see 55.90, 56.10 ', N'49.1 ', 624)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (626, 399106, 3, N'49.2 ', N'Freight rail transport ', N'491 ', N'', N'', N'', N'', N'49 ', 623)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (627, 399107, 4, N'49.20 ', N'Freight rail transport ', N'4912 ', N'This class includes:- freight transport on mainline rail networks as well as short line freight railroads ', N'', N'', N'This class excludes:- warehousing and storage, see 52.10- freight terminal activities, see 52.21- operation of railroad infrastructure; related activities such as switching and shunting, see 52.21- cargo handling, see 52.24 ', N'49.2 ', 626)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (628, 399108, 3, N'49.3 ', N'Other passenger land transport ', N'492 ', N'This group includes all land-based passenger transport activities other than rail transport. However, rail transport as part of urban or suburban transport systems is included there. ', N'', N'', N'', N'49 ', 623)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (629, 399109, 4, N'49.31 ', N'Urban and suburban passenger land transport ', N'4921 ', N'This class includes:- land transport of passengers by urban or suburban transport systems. This may include different modes of land transport, such as by motor bus, tramway, streetcar, trolley bus, underground and elevated railways etc. The transport is carried out on scheduled routes normally following a fixed time schedule, entailing the picking up and setting down of passengers at normally fixed stops. ', N'This class also includes:- town-to-airport or town-to-station lines- operation of funicular railways, aerial cableways etc. if part of urban or suburban transit systems ', N'', N'This class excludes:- passenger transport by interurban railways, see 49.10 ', N'49.3 ', 628)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (630, 399110, 4, N'49.32 ', N'Taxi operation ', N'4922 ', N'', N'This class also includes:- other rental of private cars with driver ', N'- Motorbike-taxi services- Taxi-radio services ', N'', N'49.3 ', 628)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (631, 399111, 4, N'49.39 ', N'Other passenger land transport n.e.c. ', N'4922 ', N'This class includes:- other passenger road transport:  • scheduled long-distance bus services  • charters, excursions and other occasional coach services  • airport shuttles- operation of teleferics, funiculars, ski and cable lifts if not part of urban or suburban transit systems ', N'This class also includes:- operation of school buses and buses for transport of employees- passenger transport by man- or animal-drawn vehicles ', N'- Bike-taxi services ', N'This class excludes:- ambulance transport, see 86.90 ', N'49.3 ', 628)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (632, 399112, 3, N'49.4 ', N'Freight transport by road and removal services ', N'492 ', N'This group includes all land-based freight transport activities other than rail transport. ', N'', N'', N'', N'49 ', 623)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (633, 399113, 4, N'49.41 ', N'Freight transport by road ', N'4923 ', N'This class includes:- all freight transport operations by road:  • logging haulage  • stock haulage  • refrigerated haulage  • heavy haulage  • bulk haulage, including haulage in tanker trucks including milk collection at farms  • haulage of automobiles  • transport of waste and waste materials, without collection or disposal ', N'This class also includes:- rental of trucks with driver- freight transport by man or animal-drawn vehicles ', N'- Concrete mixer lorry rental, with operator- Road transport of letters as freight, carried out by a third party on behalf of postal or courier units- Transportation or moving objects within an industrial plant, on a fee or contract basis ', N'This class excludes:- log hauling within the forest, as part of logging operations, see 02.40- distribution of water by trucks, see 36.00- operation of terminal facilities for handling freight, see 52.21- crating and packing activities for transport, see 52.29- post and courier activities, see 53.10, 53.20- waste transport as integrated part of waste collection activities, see 38.11, 38.12 ', N'49.4 ', 632)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (634, 399114, 4, N'49.42 ', N'Removal services ', N'4923 ', N'This class includes:- removal (relocation) services to businesses and households by road transport ', N'', N'', N'', N'49.4 ', 632)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (635, 399115, 3, N'49.5 ', N'Transport via pipeline ', N'493 ', N'', N'', N'', N'', N'49 ', 623)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (636, 399116, 4, N'49.50 ', N'Transport via pipeline ', N'4930 ', N'This class includes:- transport of gases, liquids, water, slurry and other commodities via pipelines ', N'This class also includes:- operation of pump stations ', N'', N'This class excludes:- distribution of natural or manufactured gas, steam or water, see 35.22, 35.30, 36.00- transport of liquids by trucks, see 49.41 ', N'49.5 ', 635)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (637, 399117, 2, N'50 ', N'Water transport ', N'50 ', N'This division includes the transport of passengers or freight over water, whether scheduled or not. Also included are the operation of towing or pushing boats, excursion, cruise or sightseeing boats, ferries, water taxis etc. Although the location is an indicator for the separation between sea and inland water transport, the deciding factor is the type of vessel used. Transport on sea-going vessels is classified in groups 50.1 and 50.2, while transport using other vessels is classified in groups 50.3 and 50.4. ', N'', N'', N'This division excludes restaurant and bar activities on board ships (see 56.10, 56.30), if carried out by separate units. ', N'H ', 622)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (638, 399118, 3, N'50.1 ', N'Sea and coastal passenger water transport ', N'501 ', N'This group includes the transport of passengers on vessels designed for operating on sea or coastal waters. ', N'Also included is the transport of passengers on great lakes etc. when similar types of vessels are used. ', N'', N'', N'50 ', 637)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (639, 399119, 4, N'50.10 ', N'Sea and coastal passenger water transport ', N'5011 ', N'This class includes:- transport of passengers over seas and coastal waters, whether scheduled or not:  • operation of excursion, cruise or sightseeing boats  • operation of ferries, water taxis etc. ', N'This class also includes:- rental of pleasure boats with crew for sea and coastal water transport (e.g. for fishing cruises) ', N'- Ferry transport of cars with driver ', N'This class excludes:- restaurant and bar activities on board ships, when provided by separate units, see 56.10, 56.30- rental of pleasure boats and yachts without crew, see 77.21- rental of commercial ships or boats without crew, see 77.34- operation of "floating casinos”, see 92.00 ', N'50.1 ', 638)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (640, 399120, 3, N'50.2 ', N'Sea and coastal freight water transport ', N'501 ', N'This group includes the transport of freight on vessels designed for operating on sea or coastal waters. ', N'Also included is the transport of freight on great lakes etc. when similar types of vessels are used. ', N'', N'', N'50 ', 637)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (641, 399121, 4, N'50.20 ', N'Sea and coastal freight water transport ', N'5012 ', N'This class includes:- transport of freight over seas and coastal waters, whether scheduled or not- transport by towing or pushing of barges, oil rigs etc. ', N'This class also includes:- rental of vessels with crew for sea and coastal freight water transport ', N'- Ferry transport of cars without driver ', N'This class excludes:- storage of freight, see 52.10- harbour operation and other auxiliary activities such as docking, pilotage, lighterage, vessel salvage, see 52.22- cargo handling, see 52.24- rental of commercial ships or boats without crew, see 77.34 ', N'50.2 ', 640)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (642, 399122, 3, N'50.3 ', N'Inland passenger water transport ', N'502 ', N'This group includes the transport of passengers on inland waters, involving vessels that are not suitable for sea transport. ', N'', N'', N'', N'50 ', 637)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (643, 399123, 4, N'50.30 ', N'Inland passenger water transport ', N'5021 ', N'This class includes:- transport of passengers via rivers, canals, lakes and other inland waterways, including inside harbours and ports ', N'This class also includes:- rental of pleasure boats with crew for inland water transport ', N'', N'This class excludes:- rental of pleasure boats and yachts without crew, see 77.21 ', N'50.3 ', 642)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (644, 399124, 3, N'50.4 ', N'Inland freight water transport ', N'502 ', N'This group includes the transport of freight on inland waters, involving vessels that are not suitable for sea transport. ', N'', N'', N'', N'50 ', 637)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (645, 399125, 4, N'50.40 ', N'Inland freight water transport ', N'5022 ', N'This class includes:- transport of freight via rivers, canals, lakes and other inland waterways, including inside harbours and ports ', N'This class also includes:- rental of vessels with crew for inland freight water transport ', N'', N'This class excludes:- cargo handling, see 52.24- rental of commercial ships or boats without crew, see 77.34 ', N'50.4 ', 644)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (646, 399126, 2, N'51 ', N'Air transport ', N'51 ', N'This division includes the transport of passengers or freight by air or via space. ', N'', N'', N'This division excludes:- crop spraying, see 01.61- repair and maintenance of aircraft or aircraft engines, see 33.16- operation of airports, see 52.23- aerial advertising (sky-writing), see 73.11- aerial photography, see 74.20 ', N'H ', 622)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (647, 399127, 3, N'51.1 ', N'Passenger air transport ', N'511 ', N'', N'', N'', N'', N'51 ', 646)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (648, 399128, 4, N'51.10 ', N'Passenger air transport ', N'5110 ', N'This class includes:- transport of passengers by air over regular routes and on regular schedules- charter flights for passengers- scenic and sightseeing flights ', N'This class also includes:- rental of air transport equipment with operator for the purpose of passenger transportation- general aviation activities, such as:  • transport of passengers by aero clubs for instruction or pleasure ', N'', N'This class excludes:- rental of air transport equipment without operator, see 77.35 ', N'51.1 ', 647)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (649, 399129, 3, N'51.2 ', N'Freight air transport and space transport ', N'512 ', N'', N'', N'', N'', N'51 ', 646)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (650, 399130, 4, N'51.21 ', N'Freight air transport ', N'5120 ', N'This class includes:- transport freight by air over regular routes and on regular schedules- non-scheduled transport of freight by air ', N'This class also includes:- rental of air transport equipment with operator for the purpose of freight transportation ', N'', N'', N'51.2 ', 649)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (651, 399131, 4, N'51.22 ', N'Space transport ', N'5120 ', N'This class includes:- launching of satellites and space vehicles- space transport of freight and passengers ', N'', N'', N'', N'51.2 ', 649)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (652, 399132, 2, N'52 ', N'Warehousing and support activities for transportation ', N'52 ', N'This division includes warehousing and support activities for transportation, such as operating of transport infrastructure (e.g. airports, harbours, tunnels, bridges, etc.), the activities of transport agencies and cargo handling. ', N'', N'', N'', N'H ', 622)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (653, 399133, 3, N'52.1 ', N'Warehousing and storage ', N'521 ', N'', N'', N'', N'', N'52 ', 652)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (654, 399134, 4, N'52.10 ', N'Warehousing and storage ', N'5210 ', N'This class includes:- operation of storage and warehouse facilities for all kinds of goods:  • operation of grain silos, general merchandise warehouses, refrigerated warehouses, storage tanks etc. ', N'This class also includes:- storage of goods in foreign trade zones- blast freezing ', N'- Digitalisation and storage of files and documents ', N'This class excludes:- parking facilities for motor vehicles, see 52.21- operation of self storage facilities, see 68.20- rental of vacant space, see 68.20 ', N'52.1 ', 653)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (655, 399135, 3, N'52.2 ', N'Support activities for transportation ', N'522 ', N'This group includes activities supporting the transport of passengers or freight, such as operation of parts of the transport infrastructure or activities related to handling freight immediately before or after transport or between transport segments. The operation and maintenance of all transport facilities is included. ', N'', N'', N'', N'52 ', 652)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (656, 399136, 4, N'52.21 ', N'Service activities incidental to land transportation ', N'5221 ', N'This class includes:- activities related to land transport of passengers, animals or freight:  • operation of terminal facilities such as railway stations, bus stations, stations for the handling of goods  • operation of railroad infrastructure  • operation of roads, bridges, tunnels, car parks or garages, bicycle parkings, winter storage of caravans- switching and shunting- towing and road side assistance ', N'This class also includes:- liquefaction of gas for transportation purposes ', N'- Liquefaction of gas and regasification for transport purposes- Ensuring safety at railroad infrastructure maintenance work- Collection of fees for public transport- Natural gas liquefaction on own account ', N'This class excludes:- cargo handling, see 52.24 ', N'52.2 ', 655)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (657, 399137, 4, N'52.22 ', N'Service activities incidental to water transportation ', N'5222 ', N'This class includes:- activities related to water transport of passengers, animals or freight:  • operation of terminal facilities such as harbours and piers  • operation of waterway locks etc.  • navigation, pilotage and berthing activities  • lighterage, salvage activities  • lighthouse activities ', N'', N'- Ensuring safety at waterway infrastructure maintenance work ', N'This class excludes:- cargo handling, see 52.24- operation of marinas, see 93.29 ', N'52.2 ', 655)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (658, 399138, 4, N'52.23 ', N'Service activities incidental to air transportation ', N'5223 ', N'This class includes:- activities related to air transport of passengers, animals or freight:  • operation of terminal facilities such as airway terminals etc.  • airport and air-traffic-control activities  • ground service activities on airfields etc. ', N'This class also includes:- fire fighting and fire prevention services at airports ', N'- Ensuring safety at airfield infrastructure maintenance work- Air traffic control activities, at airports and for long distances ', N'This class excludes:- cargo handling, see 52.24- operation of flying schools, see 85.32, 85.53 ', N'52.2 ', 655)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (659, 399139, 4, N'52.24 ', N'Cargo handling ', N'5224 ', N'This class includes:- loading and unloading of goods or passengers'' luggage irrespective of the mode of transport used for transportation- stevedoring- loading and unloading of freight railway cars ', N'', N'- Rental and leasing of transtainers or luffing cranes, with operator, for cargo handling activities ', N'This class excludes:- operation of terminal facilities, see 52.21, 52.22 and 52.23 ', N'52.2 ', 655)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (660, 399140, 4, N'52.29 ', N'Other transportation support activities ', N'5229 ', N'This class includes:- forwarding of freight- arranging or organising of transport operations by rail, road, sea or air- organisation of group and individual consignments (including pickup and delivery of goods and grouping of consignments)- issue and procurement of transport documents and waybills- activities of customs agents- activities of sea-freight forwarders and air-cargo agents- brokerage for ship and aircraft space- goods-handling operations, e.g. temporary crating for the sole purpose of protecting the goods during transit, uncrating, sampling, weighing of goods ', N'', N'', N'This class excludes:- courier activities, see 53.20- provision of motor, marine, aviation and transport insurance, see 65.12- activities of travel agencies, see 79.11- activities of tour operators, see 79.12- tourist assistance activities, see 79.90 ', N'52.2 ', 655)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (661, 399141, 2, N'53 ', N'Postal and courier activities ', N'53 ', N'This division includes postal and courier activities, such as pickup, transport and delivery of letters and parcels under various arrangements. ', N'Local delivery and messenger services are also included. ', N'', N'', N'H ', 622)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (662, 399142, 3, N'53.1 ', N'Postal activities under universal service obligation ', N'531 ', N'', N'', N'', N'', N'53 ', 661)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (663, 399143, 4, N'53.10 ', N'Postal activities under universal service obligation ', N'5310 ', N'This class includes the activities of postal services operating under a universal service obligation by one or more designated universal service providers. The activities include use of the universal service infrastructure, including retail locations, sorting and processing facilities, and carrier routes to pickup and deliver the mail. The delivery can include letter-post, i.e. letters, postcards, printed papers (newspaper, periodicals, advertising items, etc.), small packets, goods or documents. Also included are other services necessary to support the universal service obligation.This class includes:- pickup, sorting, transport and delivery (domestic or international) of letter-post and (mail-type) parcels and packages by postal services operating under a universal service obligation. One or more modes of transport may be involved and the activity may be carried out with either self-owned (private) transport or via public transport.- collection of letter-mail and parcels from public letter-boxes or from post offices ', N'', N'', N'This class excludes:- postal giro, postal savings activities and money order activities, see 64.19 ', N'53.1 ', 662)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (664, 399144, 3, N'53.2 ', N'Other postal and courier activities ', N'532 ', N'', N'', N'', N'', N'53 ', 661)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (665, 399145, 4, N'53.20 ', N'Other postal and courier activities ', N'5320 ', N'This class includes:- pickup, sorting, transport and delivery (domestic or international) of letter-post and (mail-type) parcels and packages by firms operating outside the scope of a universal service obligation. One or more modes of transport may be involved and the activity may be carried out with either self-owned (private) transport or via public transport. ', N'This class also includes:- home delivery services ', N'', N'This class excludes:- transport of freight, see (according to mode of transport) 49.20, 49.41, 50.20, 50.40, 51.21, 51.22 ', N'53.2 ', 664)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (666, 399146, 1, N'I ', N'ACCOMMODATION AND FOOD SERVICE ACTIVITIES ', N'I ', N'This section includes the provision of short-stay accommodation for visitors and other travellers and the provision of complete meals and drinks fit for immediate consumption. The amount and type of supplementary services provided within this section can vary widely. ', N'', N'', N'This section excludes the provision of long-term accommodation as primary residences, which is classified in real estate activities (section L). Also excluded is the preparation of food or drinks that are either not fit for immediate consumption or that are sold through independent distribution channels, i.e. through wholesale or retail trade activities. The preparation of these foods is classified in manufacturing (section C). ', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (667, 399147, 2, N'55 ', N'Accommodation ', N'55 ', N'This division includes the provision of short-stay accommodation for visitors and other travellers. ', N'Also included is the provision of longer term accommodation for students, workers and similar individuals. Some units may provide only accommodation while others provide a combination of accommodation, meals and/or recreational facilities. ', N'', N'This division excludes activities related to the provision of long-term primary residences in facilities such as apartments typically leased on a monthly or annual basis classified in real estate (section L). ', N'I ', 666)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (668, 399148, 3, N'55.1 ', N'Hotels and similar accommodation ', N'551 ', N'', N'', N'', N'', N'55 ', 667)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (669, 399149, 4, N'55.10 ', N'Hotels and similar accommodation ', N'5510 ', N'This class includes the provision of accommodation, typically on a daily or weekly basis, principally for short stays by visitors. This includes the provision of furnished accommodation in guest rooms and suites. Services include daily cleaning and bed-making. A range of additional services may be provided such as food and beverage services, parking, laundry services, swimming pools and exercise rooms, recreational facilities as well as conference and convention facilities.This class includes accommodation provided by:- hotels- resort hotels - suite/apartment hotels- motels ', N'', N'- Bed and breakfast units, with daily room cleaning and bed making ', N'This class excludes:- provision of homes and furnished or unfurnished flats or apartments for more permanent use, typically on a monthly or annual basis, see division 68 ', N'55.1 ', 668)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (670, 399150, 3, N'55.2 ', N'Holiday and other short-stay accommodation ', N'551 ', N'', N'', N'', N'', N'55 ', 667)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (671, 399151, 4, N'55.20 ', N'Holiday and other short-stay accommodation ', N'5510 ', N'This class includes the provision of accommodation, typically on a daily or weekly basis, principally for short stays by visitors, in self-contained space consisting of complete furnished rooms or areas for living/dining and sleeping, with cooking facilities or fully equipped kitchens. This may take the form of apartments or flats in small free-standing multi-storey buildings or clusters of buildings, or single storey bungalows, chalets, cottages and cabins. Very minimal complementary services, if any, are provided. This class includes accommodation provided by:- children''s and other holiday homes- visitor flats and bungalows- cottages and cabins without housekeeping services- youth hostels and mountain refuges ', N'', N'- Hotels with self-service, without daily room cleaning and bed making- Bed and breakfast units, without daily room cleaning and bed making ', N'This class excludes:- provision of furnished short-stay accommodation with daily cleaning, bed-making, food and beverage services, see 55.10- provision of homes and furnished or unfurnished flats or apartments for more permanent use, typically on a monthly or annual basis, see division 68 ', N'55.2 ', 670)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (672, 399152, 3, N'55.3 ', N'Camping grounds, recreational vehicle parks and trailer parks ', N'552 ', N'', N'', N'', N'', N'55 ', 667)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (673, 399153, 4, N'55.30 ', N'Camping grounds, recreational vehicle parks and trailer parks ', N'5520 ', N'This class includes:- provision of accommodation in campgrounds, trailer parks, recreational camps and fishing and hunting camps for short stay visitors- provision of space and facilities for recreational vehicles ', N'This class also includes accommodation provided by:- protective shelters or plain bivouac facilities for placing tents and/or sleeping bags ', N'', N'This class excludes:- mountain refuge, cabins and hostels, see 55.20 ', N'55.3 ', 672)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (674, 399154, 3, N'55.9 ', N'Other accommodation ', N'559 ', N'', N'', N'', N'', N'55 ', 667)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (675, 399155, 4, N'55.90 ', N'Other accommodation ', N'5590 ', N'This class includes the provision temporary or longer-term accommodation in single or shared rooms or dormitories for students, migrant (seasonal) workers and other individuals. This class includes:- student residences- school dormitories- workers hostels - rooming and boarding houses- railway sleeping cars ', N'', N'', N'', N'55.9 ', 674)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (676, 399156, 2, N'56 ', N'Food and beverage service activities ', N'56 ', N'This division includes food and beverage serving activities providing complete meals or drinks fit for immediate consumption, whether in traditional restaurants, self-service or take-away restaurants, whether as permanent or temporary stands with or without seating. Decisive is the fact that meals fit for immediate consumption are offered, not the kind of facility providing them. ', N'', N'', N'Excluded is the production of meals not fit for immediate consumption or not planned to be consumed immediately or of prepared food which is not considered to be a meal (see divisions 10: manufacture of food products and 11: manufacture of beverages). Also excluded is the sale of not self-manufactured food that is not considered to be a meal or of meals that are not fit for immediate consumption (see section G: wholesale and retail trade). ', N'I ', 666)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (677, 399157, 3, N'56.1 ', N'Restaurants and mobile food service activities ', N'561 ', N'', N'', N'', N'', N'56 ', 676)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (678, 399158, 4, N'56.10 ', N'Restaurants and mobile food service activities ', N'5610 ', N'This class includes the provision of food services to customers, whether they are served while seated or serve themselves from a display of items, whether they eat the prepared meals on the premises, take them out or have them delivered. This includes the preparation and serving of meals for immediate consumption from motorised vehicles or non-motorised carts.This class includes activities of:- restaurants- cafeterias- fast-food restaurants- take-out eating places- ice cream truck vendors- mobile food carts- food preparation in market stalls ', N'This class also includes:- restaurant and bar activities connected to transportation, when carried out by separate units ', N'- Delivery of self-prepared meals, such as pizza and sushi, for immediate consumption ', N'This class excludes:- retail sale of food through vending machines, see 47.99- concession operation of eating facilities, see 56.29 ', N'56.1 ', 677)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (679, 399159, 3, N'56.2 ', N'Event catering and other food service activities ', N'562 ', N'This group includes catering activities for individual events or for a specified period of time and the operation of food concessions, such as at sports or similar facilities. ', N'', N'', N'', N'56 ', 676)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (680, 399160, 4, N'56.21 ', N'Event catering activities ', N'5621 ', N'This class includes the provision of food services based on contractual arrangements with the customer, at the location specified by the customer, for a specific event. ', N'', N'', N'This class excludes:- manufacture of perishable food items for resale, see 10.89- retail sale of perishable food items, see division 47 ', N'56.2 ', 679)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (681, 399161, 4, N'56.29 ', N'Other food service activities ', N'5629 ', N'This class includes industrial catering, i.e. the provision of food services based on contractual arrangements with the customer, for a specific period of time. Also included is the operation of food concessions at sports and similar facilities. The food is usually prepared in a central unit.This class includes:- activities of food service contractors (e.g. for transportation companies)- operation of food concessions at sports and similar facilities- operation of canteens or cafeterias (e.g. for factories, offices, hospitals or schools) on a concession basis ', N'', N'', N'This class excludes:- manufacture of perishable food items for resale, see 10.89- retail sale of perishable food items, see division 47 ', N'56.2 ', 679)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (682, 399162, 3, N'56.3 ', N'Beverage serving activities ', N'563 ', N'', N'', N'', N'', N'56 ', 676)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (683, 399163, 4, N'56.30 ', N'Beverage serving activities ', N'5630 ', N'This class includes preparation and serving of beverages for immediate consumption on the premises.This class includes activities of:- bars- taverns- cocktail lounges- discotheques (with beverage serving predominant)- beer parlours- coffee shops- fruit juice bars- mobile beverage vendors ', N'', N'', N'This class excludes:- reselling packaged/prepared beverages, see 47- retail sale of beverages through vending machines, see 47.99- operation of discotheques and dance floors without beverage serving, see 93.29 ', N'56.3 ', 682)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (684, 399164, 1, N'J ', N'INFORMATION AND COMMUNICATION ', N'J ', N'This section includes the production and distribution of information and cultural products, the provision of the means to transmit or distribute these products, as well as data or communications, information technology activities and the processing of data and other information service activities.The main components of this section are publishing activities (division 58), including software publishing, motion picture and sound recording activities (division 59), radio and TV broadcasting and programming activities (division 60), telecommunications activities (division 61), information technology activities (division 62) and other information service activities (division 63).Publishing includes the acquisition of copyrights for content (information products) and making this content available to the general public by engaging in (or arranging for) the reproduction and distribution of this content in various forms. All the feasible forms of publishing (in print, electronic or audio form, on the Internet, as multimedia products such as CD-ROM reference books etc.) are included in this section.Activities related to production and distribution of TV programming span divisions 59, 60 and 61, reflecting different stages in this process. Individual components, such as movies, television series etc. are produced by activities in division 59, while the creation of a complete television channel programme, from components produced in division 59 or other components (such as live news programming) is included in division 60. Division 60 also includes the broadcasting of this programme by the producer. The distribution of the complete television programme by third parties, i.e. without any alteration of the content, is included in division 61. This distribution in division 61 can be done through broadcasting, satellite or cable systems. ', N'', N'', N'', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (685, 399165, 2, N'58 ', N'Publishing activities ', N'58 ', N'This division includes the publishing of books, brochures, leaflets, dictionaries, encyclopaedias, atlases, maps and charts; publishing of newspapers, journals and periodicals; directory and mailing list and other publishing, as well as software publishing.Publishing includes the acquisition of copyrights to content (information products) and making this content available to the general public by engaging in (or arranging for) the reproduction and distribution of this content in various forms. All the feasible forms of publishing (in print, electronic or audio form, on the Internet, as multimedia products such as CD-ROM reference books etc.), except publishing of motion pictures, are included in this division. ', N'', N'', N'This division excludes the publishing of motion pictures, video tapes and movies on DVD or similar media (division 59) and the production of master copies for records or audio material (division 59). Also excluded is printing (see 18.11, 18.12) and the mass reproduction of recorded media (see 18.20). ', N'J ', 684)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (686, 399166, 3, N'58.1 ', N'Publishing of books, periodicals and other publishing activities ', N'581 ', N'This group includes activities of publishing books, newspapers, magazines and other periodicals, directories and mailing lists, and other works such as photos, engravings, postcards, timetables, forms, posters and reproductions of works of art. These works are characterised by the intellectual creativity required in their development and are usually protected by copyright. ', N'', N'', N'', N'58 ', 685)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (687, 399167, 4, N'58.11 ', N'Book publishing ', N'5811 ', N'This class includes the activities of publishing of books in print, electronic (CD, electronic displays etc.) or audio form or on the Internet.Included are:- publishing of books, brochures, leaflets and similar publications, including publishing of dictionaries and encyclopaedias- publishing of atlases, maps and charts- publishing of audio books- publishing of encyclopaedias etc. on CD-ROM ', N'', N'', N'This class excludes:- production of globes, see 32.99- publishing of advertising material, see 58.19- publishing of music and sheet books, see 59.20- activities of independent authors, see 90.03 ', N'58.1 ', 686)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (688, 399168, 4, N'58.12 ', N'Publishing of directories and mailing lists ', N'5812 ', N'This class includes the publishing of lists of facts/information (databases), that are protected in their form, but not in their content. These lists can be published in printed or electronic form.This class includes:- publishing of mailing lists- publishing of telephone books- publishing of other directories and compilations, such as case law, pharmaceutical compendia etc. ', N'', N'', N'', N'58.1 ', 686)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (689, 399169, 4, N'58.13 ', N'Publishing of newspapers ', N'5813 ', N'This class includes the publishing of newspapers, including advertising newspapers, appearing at least four times a week. Publishing can be done in print or electronic form, including on the Internet. ', N'', N'', N'This class excludes:- news agency activities, see 63.91 ', N'58.1 ', 686)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (690, 399170, 4, N'58.14 ', N'Publishing of journals and periodicals ', N'5813 ', N'This class includes the publishing of periodicals and other journals, appearing less than four times a week. Publishing can be done in print or electronic form, including on the Internet. Publishing of radio and television schedules is included here. ', N'', N'', N'', N'58.1 ', 686)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (691, 399171, 4, N'58.19 ', N'Other publishing activities ', N'5819 ', N'This class includes:- publishing (including on-line) of:  • catalogues   • photos, engravings and postcards  • greeting cards  • forms  • posters, reproduction of works of art  • advertising material  • other printed matter- on-line publishing of statistics and other information ', N'', N'- Publishing of blogs ', N'This class excludes:- publishing of advertising newspapers, see 58.13- on-line provision of software (application hosting and application service provisioning), see 63.11 ', N'58.1 ', 686)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (692, 399172, 3, N'58.2 ', N'Software publishing ', N'582 ', N'', N'', N'', N'', N'58 ', 685)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (693, 399173, 4, N'58.21 ', N'Publishing of computer games ', N'5820 ', N'This class includes: - publishing of computer games for all platforms ', N'', N'- Operation of On-line games ', N'', N'58.2 ', 692)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (694, 399174, 4, N'58.29 ', N'Other software publishing ', N'5820 ', N'This class includes:- publishing of ready-made (non-customised) software, including translation or adaptation of non-customised software for a particular market on own account:  • operating systems  • business and other applications ', N'', N'- Development and publishing of software for microchip design ', N'This class excludes:- reproduction of software, see 18.20- retail sale of non-customised software, see 47.41- production of software not associated with publishing, including translation or adaptation of non-customised software for a particular market on a fee or contract basis, see 62.01- on-line provision of software (application hosting and application service provisioning), see 63.11 ', N'58.2 ', 692)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (695, 399175, 2, N'59 ', N'Motion picture, video and television programme production, sound recording and music publishing activities ', N'59 ', N'This division includes production of theatrical and non-theatrical motion pictures whether on film, video tape or disc for direct projection in theatres or for broadcasting on television; supporting activities such as film editing, cutting, dubbing etc.; distribution of motion pictures and other film productions to other industries; as well as motion picture or other film productions projection. Buying and selling of motion picture or other film productions distribution rights is also included. ', N'This division also includes the sound recording activities, i.e. production of original sound master recordings, releasing, promoting and distributing them, publishing of music as well as sound recording service activities in a studio or elsewhere. ', N'', N'', N'J ', 684)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (696, 399176, 3, N'59.1 ', N'Motion picture, video and television programme activities ', N'591 ', N'This group includes production of theatrical and non-theatrical motion pictures whether on film, video tape, DVD or other media, including digital distribution, for direct projection in theatres or for broadcasting on television; supporting activities such as film editing, cutting, dubbing etc.; distribution of motion pictures or other film productions (video tapes, DVDs, etc) to other industries; as well as their projection. ', N'Buying and selling of motion picture or any other film production distribution rights is also included. ', N'', N'', N'59 ', 695)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (697, 399177, 4, N'59.11 ', N'Motion picture, video and television programme production activities ', N'5911 ', N'This class includes:- production of motion pictures, videos, television programmes (televisions series, documentaries etc.), or television advertisements ', N'', N'', N'This class excludes:- film duplicating (except reproduction of motion picture film for theatrical distribution) as well as audio and video tape, CD or DVD reproduction from master copies, see 18.20- wholesale of recorded video tapes, CDs, DVDs, see 46.43- wholesale of blank video tapes, CDs, see 46.52- retail trade of video tapes, CDs, DVDs, see 47.63- post-production activities, see 59.12- sound recording and recording of books on tape, see 59.20- television broadcasting, see 60.2- creating a complete television channel programme, see 60.2- film processing other than for the motion picture industry, see 74.20- activities of personal theatrical or artistic agents or agencies, see 74.90- rental of video tapes, DVDs to the general public, see 77.22- real-time (i.e. simultaneous) closed captioning of live television performances, meetings, conferences, etc., see 82.99- activities of own account actors, cartoonists, directors, stage designers and technical specialists, see 90.0 ', N'59.1 ', 696)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (698, 399178, 4, N'59.12 ', N'Motion picture, video and television programme post-production activities ', N'5912 ', N'This class includes post-production activities such as editing, film/tape transfers, titling, subtitling, credits, closed captioning, computer-produced graphics, animation and special effects, developing and processing motion picture film, as well as activities of motion picture film laboratories and activities of special laboratories for animated films. ', N'This class also includes:- activities of stock footage film libraries etc. ', N'', N'This class excludes:- film duplicating (except reproduction of motion picture film for theatrical distribution) as well as audio and video tape, CD or DVD reproduction from master copies, see 18.20- wholesale of recorded video tapes, CDs, DVDs, see 46.43- wholesale of blank video tapes, CDs, see 46.52- retail trade of video tapes, CDs, DVDs, see 47.63- film processing other than for the motion picture industry, see 74.20- rental of video tapes, DVDs to the general public, see 77.22- activities of own account actors, cartoonists, directors, stage designers and technical specialists, see 90.0 ', N'59.1 ', 696)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (699, 399179, 4, N'59.13 ', N'Motion picture, video and television programme distribution activities ', N'5913 ', N'This class includes:- distributing film, video tapes, DVDs and similar productions to motion picture theatres, television networks and stations, and exhibitors ', N'This class also includes:- acquiring film, video tape and DVD distribution rights ', N'', N'This class excludes:- film duplicating as well as audio and video tape, CD or DVD reproduction from master copies, see 18.20- wholesale of recorded video tapes and DVDs, see 46.43- retail sale of recorded video tapes and DVDs, see 47.63 ', N'59.1 ', 696)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (700, 399180, 4, N'59.14 ', N'Motion picture projection activities ', N'5914 ', N'This class includes:- activities of motion picture or video tape projection in cinemas, in the open air or in other projection facilities- activities of cine-clubs ', N'', N'', N'', N'59.1 ', 696)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (701, 399181, 3, N'59.2 ', N'Sound recording and music publishing activities ', N'592 ', N'', N'', N'', N'', N'59 ', 695)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (702, 399182, 4, N'59.20 ', N'Sound recording and music publishing activities ', N'5920 ', N'This class includes the activities of production of original (sound) master recordings, such as tapes, CDs; releasing, promoting and distributing sound recordings to wholesalers, retailers or directly to the public. These activities might be integrated or not with the production of master recordings in the same unit. If not, the unit exercising these activities has to obtain the reproduction and distribution rights to master recordings. ', N'This class also includes sound recording service activities in a studio or elsewhere, including the production of taped ( i.e. non-live) radio programming.This class also includes the activities of music publishing, i.e. activities of acquiring and registering copyrights for musical compositions, promoting, authorising and using these compositions in recordings, radio, television, motion pictures, live performances, print and other media. Units engaged in these activities may own the copyright or act as administrator of the music copyrights on behalf of the copyright owners. Publishing of music and sheet books is included here. ', N'', N'', N'59.2 ', 701)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (703, 399183, 2, N'60 ', N'Programming and broadcasting activities ', N'60 ', N'This division includes the activities of creating content or acquiring the right to distribute content and subsequently broadcasting that content, such as radio, television and data programs of entertainment, news, talk, and the like. Also included is data broadcasting, typically integrated with radio or TV broadcasting. The broadcasting can be performed using different technologies, over-the-air, via satellite, via a cable network or via Internet. ', N'This division also includes the production of programs that are typically narrowcast in nature (limited format, such as news, sports, education, and youth-oriented programming) on a subscription or fee basis, to a third party, for subsequent broadcasting to the public. ', N'', N'This division excludes the distribution of cable and other subscription programming (see division 61). ', N'J ', 684)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (704, 399184, 3, N'60.1 ', N'Radio broadcasting ', N'601 ', N'', N'', N'', N'', N'60 ', 703)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (705, 399185, 4, N'60.10 ', N'Radio broadcasting ', N'6010 ', N'This class includes:- activities of broadcasting audio signals through radio broadcasting studios and facilities for the transmission of aural programming to the public, to affiliates or to subscribers ', N'This class also includes:- activities of radio networks, i.e. assembling and transmitting aural programming to the affiliates or subscribers via over-the-air broadcasts, cable or satellite- radio broadcasting activities over the Internet (Internet radio stations) - data broadcasting integrated with radio broadcasting ', N'', N'This class excludes:- the production of taped radio programming, see 59.20 ', N'60.1 ', 704)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (706, 399186, 3, N'60.2 ', N'Television programming and broadcasting activities ', N'602 ', N'', N'', N'', N'', N'60 ', 703)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (707, 399187, 4, N'60.20 ', N'Television programming and broadcasting activities ', N'6020 ', N'This class includes the creation of a complete television channel programme, from purchased programme components (e.g. movies, documentaries etc.), self produced programme components (e.g. local news, live reports) or a combination thereof. This complete television programme can be either broadcast by the producing unit or produced for transmission by a third party distributor, such as cable companies or satellite television providers.The programming may be of a general or specialised nature (e.g. limited formats such as news, sports, education or youth oriented programming). This class includes programming that is made freely available to users, as well as programming that is available only on a subscription basis. The programming of video-on-demand channels is also included here. ', N'This class also includes data broadcasting integrated with television broadcasting. ', N'- Broadcasting of live theatrical performances via Internet ', N'This class excludes:- the production of television programme elements (movies, documentaries, talk shows, commercials etc.) not associated with broadcasting, see 59.11- the assembly of a package of channels and distribution of that package, without programming, see division 61 ', N'60.2 ', 706)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (708, 399188, 2, N'61 ', N'Telecommunications ', N'61 ', N'This division includes the activities of providing telecommunications and related service activities, that is transmitting voice, data, text, sound and video. The transmission facilities that carry out these activities may be based on a single technology or a combination of technologies. The commonality of activities classified in this division is the transmission of content, without being involved in its creation. The breakdown in this division is based on the type of infrastructure operated.In the case of transmission of television signals this may include the bundling of complete programming channels (produced in division 60) in to programme packages for distribution. ', N'', N'', N'', N'J ', 684)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (709, 399189, 3, N'61.1 ', N'Wired telecommunications activities ', N'611 ', N'', N'', N'', N'', N'61 ', 708)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (710, 399190, 4, N'61.10 ', N'Wired telecommunications activities ', N'6110 ', N'This class includes:- operating, maintaining or providing access to facilities for the transmission of voice, data, text, sound and video using a wired telecommunications infrastructure, including:  • operating and maintaining switching and transmission facilities to provide point-to-point communications via landlines, microwave or a combination of landlines and satellite linkups  • operating of cable distribution systems (e.g. for distribution of data and television signals)  • furnishing telegraph and other non-vocal communications using own facilitiesThe transmission facilities that carry out these activities, may be based on a single technology or a combination of technologies. ', N'This class also includes:- purchasing access and network capacity from owners and operators of networks and providing telecommunications services using this capacity to businesses and households- provision of Internet access by the operator of the wired infrastructure ', N'', N'This class excludes:- telecommunications resellers, see 61.90 ', N'61.1 ', 709)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (711, 399191, 3, N'61.2 ', N'Wireless telecommunications activities ', N'612 ', N'', N'', N'', N'', N'61 ', 708)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (712, 399192, 4, N'61.20 ', N'Wireless telecommunications activities ', N'6120 ', N'This class includes:- operating, maintaining or providing access to facilities for the transmission of voice, data, text, sound, and video using a wireless telecommunications infrastructure- maintaining and operating paging as well as cellular and other wireless telecommunications networksThe transmission facilities provide omni-directional transmission via airwaves and may be based on a single technology or a combination of technologies. ', N'This class also includes:- purchasing access and network capacity from owners and operators of networks and providing wireless telecommunications services (except satellite) using this capacity to businesses and households- provision of Internet access by the operator of the wireless infrastructure ', N'', N'This class excludes:- telecommunications resellers, see 61.90 ', N'61.2 ', 711)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (713, 399193, 3, N'61.3 ', N'Satellite telecommunications activities ', N'613 ', N'', N'', N'', N'', N'61 ', 708)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (714, 399194, 4, N'61.30 ', N'Satellite telecommunications activities ', N'6130 ', N'This class includes:- operating, maintaining or providing access to facilities for the transmission of voice, data, text, sound and video using a satellite telecommunications infrastructure- delivery of visual, aural or textual programming received from cable networks, local television stations or radio networks to consumers via direct-to-home satellite systems. (The units classified here do not generally originate programming material.) ', N'This class also includes:- provision of Internet access by the operator of the satellite infrastructure ', N'', N'This class excludes:- telecommunications resellers, see 61.90 ', N'61.3 ', 713)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (715, 399195, 3, N'61.9 ', N'Other telecommunications activities ', N'619 ', N'', N'', N'', N'', N'61 ', 708)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (716, 399196, 4, N'61.90 ', N'Other telecommunications activities ', N'6190 ', N'This class includes:- provision of specialised telecommunications applications, such as satellite tracking, communications telemetry, and radar station operations- operation of satellite terminal stations and associated facilities operationally connected with one or more terrestrial communications systems and capable of transmitting telecommunications to or receiving telecommunications from satellite systems- provision of Internet access over networks between the client and the ISP not owned or controlled by the ISP, such as dial-up Internet access etc.- provision of telephone and Internet access in facilities open to the public- provision of telecommunications services over existing telecom connections:  • VOIP (Voice Over Internet Protocol) provision- telecommunications resellers (i.e. purchasing and reselling network capacity without providing additional services) ', N'', N'- Transmission via Internet of text messaging (SMS)- Wholesale trade of internet access packages and phone cards- Internet cafes- Sale of prepaid telephone cards for mobile phones ', N'This class excludes:- provision of Internet access by operators of telecommunications infrastructure, see 61.10, 61.20, 61.30 ', N'61.9 ', 715)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (717, 399197, 2, N'62 ', N'Computer programming, consultancy and related activities ', N'62 ', N'This division includes the following activities of providing expertise in the field of information technologies: writing, modifying, testing and supporting software; planning and designing computer systems that integrate computer hardware, software and communication technologies; on-site management and operation of clients'' computer systems and/or data processing facilities; and other professional and technical computer-related activities. ', N'', N'', N'', N'J ', 684)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (718, 399198, 3, N'62.0 ', N'Computer programming, consultancy and related activities ', N'620 ', N'', N'', N'', N'', N'62 ', 717)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (719, 399199, 4, N'62.01 ', N'Computer programming activities ', N'6201 ', N'This class includes the writing, modifying, testing and supporting of software.This class includes:- designing the structure and content of, and/or writing the computer code necessary to create and implement:  • systems software (including updates and patches)  • software applications (including updates and patches)  • databases  • web pages- customising of software, i.e. modifying and configuring an existing application so that it is functional within the clients'' information system environment ', N'', N'', N'This class excludes:- publishing packaged software, see 58.29- translation or adaptation of non-customised software for a particular market on own account, see 58.29- planning and designing computer systems that integrate computer hardware, software and communication technologies, even though providing software might be an integral part, see 62.02 ', N'62.0 ', 718)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (720, 399200, 4, N'62.02 ', N'Computer consultancy activities ', N'6202 ', N'This class includes the planning and designing of computer systems which integrate computer hardware, software and communication technologies. Services may include related users training. ', N'', N'', N'This class excludes:- sale of computer hardware or software, see 46.51, 47.41- installation of mainframe and similar computers, see 33.20- installation (setting-up) of personal computers, see 62.09- installation of software, computer disaster recovery, see 62.09 ', N'62.0 ', 718)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (721, 399201, 4, N'62.03 ', N'Computer facilities management activities ', N'6202 ', N'This class includes the provision of on-site management and operation of clients'' computer systems and/or data processing facilities, as well as related support services. ', N'', N'', N'', N'62.0 ', 718)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (722, 399202, 4, N'62.09 ', N'Other information technology and computer service activities ', N'6209 ', N'This class includes other information technology and computer related activities not elsewhere classified, such as:- computer disaster recovery services- installation (setting-up) of personal computers- software installation services ', N'', N'', N'This class excludes:- installation of mainframe and similar computers, see 33.20- computer programming, see 62.01- computer consultancy, see 62.02- computer facilities management, see 62.03- data processing and hosting, see 63.11 ', N'62.0 ', 718)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (723, 399203, 2, N'63 ', N'Information service activities ', N'63 ', N'This division includes the activities of web search portals, data processing and hosting activities, as well as other activities that primarily supply information. ', N'', N'', N'', N'J ', 684)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (724, 399204, 3, N'63.1 ', N'Data processing, hosting and related activities; web portals ', N'631 ', N'This group includes the provision of infrastructure for hosting, data processing services and related activities, as well as the provision of search facilities and other portals for the Internet. ', N'', N'', N'', N'63 ', 723)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (725, 399205, 4, N'63.11 ', N'Data processing, hosting and related activities ', N'6311 ', N'This class includes:- provision of infrastructure for hosting, data processing services and related activities- specialized hosting activities such as:  • Web hosting  • streaming services  • application hosting- application service provisioning- general time-share provision of mainframe facilities to clients- data processing activities:  • complete processing of data supplied by clients  • generation of specialized reports from data supplied by clients- provision of data entry services ', N'', N'', N'This class excludes:- activities where the supplier uses the computers only as a tool are classified according to the nature of the services rendered ', N'63.1 ', 724)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (726, 399206, 4, N'63.12 ', N'Web portals ', N'6312 ', N'This class includes: - operation of web sites that use a search engine to generate and maintain extensive databases of Internet addresses and content in an easily searchable format- operation of other websites that act as portals to the Internet, such as media sites providing periodically updated content ', N'', N'', N'This class excludes:- publishing of books, newspapers, journals etc. via Internet, see division 58- broadcasting via Internet, see division 60 ', N'63.1 ', 724)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (727, 399207, 3, N'63.9 ', N'Other information service activities ', N'639 ', N'This group includes the activities of news agencies and all other remaining information service activities. ', N'', N'', N'This group excludes:- activities of libraries and archives, see 91.01 ', N'63 ', 723)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (728, 399208, 4, N'63.91 ', N'News agency activities ', N'6391 ', N'This class includes:- news syndicate and news agency activities furnishing news, pictures and features to the media ', N'', N'', N'This class excludes:- activities of independent photojournalists, see 74.20- activities of independent journalists, see 90.03 ', N'63.9 ', 727)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (729, 399209, 4, N'63.99 ', N'Other information service activities n.e.c. ', N'6399 ', N'This class includes other information service activities not elsewhere classified such as:- computer-based telephone information services- information search services on a contract or fee basis- news clipping services, press clipping services, etc. ', N'', N'- Sports live data collection activities ', N'This class excludes:- activities of call centres, see 82.20 ', N'63.9 ', 727)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (730, 399210, 1, N'K ', N'FINANCIAL AND INSURANCE ACTIVITIES ', N'K ', N'This section includes financial service activities, including insurance, reinsurance and pension funding activities and activities to support financial services. ', N'This section also includes the activities of holding assets, such as activities of holding companies and the activities of trusts, funds and similar financial entities. ', N'', N'', N'', NULL)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (731, 399211, 2, N'64 ', N'Financial service activities, except insurance and pension funding ', N'64 ', N'This division includes the activities of obtaining and redistributing funds other than for the purpose of insurance or pension funding or compulsory social security.Note: National institutional arrangements are likely to play a significant role in determining the classification within this division. ', N'', N'', N'', N'K ', 730)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (732, 399212, 3, N'64.1 ', N'Monetary intermediation ', N'641 ', N'This group includes the obtaining of funds in the form of transferable deposits, i.e. funds that are fixed in money terms, obtained on a day-to-day basis and, apart from central banking, obtained from non-financial sources. ', N'', N'', N'', N'64 ', 731)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (733, 399213, 4, N'64.11 ', N'Central banking ', N'6411 ', N'This class includes:- issuing and managing the country''s currency- monitoring and control of the money supply- taking deposits that are used for clearance between financial institutions- supervising banking operations- holding the country''s international reserves- acting as banker to the governmentThe activities of central banks will vary for institutional reasons. ', N'', N'', N'', N'64.1 ', 732)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (734, 399214, 4, N'64.19 ', N'Other monetary intermediation ', N'6419 ', N'This class includes the receiving of deposits and/or close substitutes for deposits and extending of credit or lending funds. The granting of credit can take a variety of forms, such as loans, mortgages, credit cards etc. These activities are generally carried out by monetary institutions other than central banks, such as:- banks- savings banks- credit unions ', N'This class also includes:- postal giro and postal savings bank activities- credit granting for house purchase by specialised deposit-taking institutions- money order activities ', N'- Electronic currency issuing and trading ', N'This class excludes:- credit granting for house purchase by specialised non-depository institutions, see 64.92- credit card transaction processing and settlement activities, see 66.19 ', N'64.1 ', 732)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (735, 399215, 3, N'64.2 ', N'Activities of holding companies ', N'642 ', N'', N'', N'', N'', N'64 ', 731)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (736, 399216, 4, N'64.20 ', N'Activities of holding companies ', N'6420 ', N'This class includes the activities of holding companies, i.e. units that hold the assets (owning controlling-levels of equity) of a group of subsidiary corporations and whose principal activity is owning the group. The holding companies in this class do not provide any other service to the businesses in which the equity is held, i.e. they do not administer or manage other units. ', N'', N'', N'This class excludes:- active management of companies and enterprises, strategic planning and decision making of the company, see 70.10 ', N'64.2 ', 735)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (737, 399217, 3, N'64.3 ', N'Trusts, funds and similar financial entities ', N'643 ', N'', N'', N'', N'', N'64 ', 731)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (738, 399218, 4, N'64.30 ', N'Trusts, funds and similar financial entities ', N'6430 ', N'This class includes legal entities organised to pool securities or other financial assets, without managing, on behalf of shareholders or beneficiaries. The portfolios are customised to achieve specific investment characteristics, such as diversification, risk, rate of return and price volatility. These entities earn interest, dividends and other property income, but have little or no employment and no revenue from the sale of services.This class includes:- open-end investment funds- closed-end investment funds- trusts, estates or agency accounts, administered on behalf of the beneficiaries under the terms of a trust agreement, will or agency agreement- unit investment trust funds ', N'', N'', N'This class excludes:- funds and trusts that earn revenue from the sale of goods or services, see NACE class according to their principal activity- activities of holding companies, see 64.20- pension funding, see 65.30- management of funds, see 66.30 ', N'64.3 ', 737)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (739, 399219, 3, N'64.9 ', N'Other financial service activities, except insurance and pension funding ', N'649 ', N'This group includes financial service activities other than those conducted by monetary institutions. ', N'', N'', N'This group excludes:- insurance and pension funding, see division 65 ', N'64 ', 731)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (740, 399220, 4, N'64.91 ', N'Financial leasing ', N'6491 ', N'This class includes:- leasing where the term approximately covers the expected life of the asset and the lessee acquires substantially all the benefits of its use and takes all the risks associated with its ownership. The ownership of the asset may or may not eventually be transferred. Such leases cover all or virtually all costs including interest. ', N'', N'', N'This class excludes:- operational leasing, see division 77, according to type of goods leased ', N'64.9 ', 739)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (741, 399221, 4, N'64.92 ', N'Other credit granting ', N'6492 ', N'This class includes:- financial service activities primarily concerned with making loans by institutions not involved in monetary intermediation, where the granting of credit can take a variety of forms, such as loans, mortgages, credit cards etc., providing the following types of services:  • granting of consumer credit  • international trade financing  • provision of long-term finance to industry by industrial banks  • money lending outside the banking system  • credit granting for house purchase by specialised non-depository institutions  • pawnshops and pawnbrokers ', N'', N'', N'This class excludes:- credit granting for house purchase by specialised institutions that also take deposits, see 64.19- operational leasing, see division 77, according to type of goods leased- grant giving activities by membership organisations, see 94.99 ', N'64.9 ', 739)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (742, 399222, 4, N'64.99 ', N'Other financial service activities, except insurance and pension funding n.e.c. ', N'6499 ', N'This class includes:- other financial service activities primarily concerned with distributing funds other than by making loans:  • factoring activities  • writing of swaps, options and other hedging arrangements  • activities of viatical settlement companies- own-account investment activities, such as by venture capital companies, investment clubs etc. ', N'', N'- Acting as a buyer for the seller of swaps and options, and as seller for the buyer of swaps and options, i.e. acting as both buyer and seller, i.e. on own account- Purchase and brokerage of receivables ', N'This class excludes:- financial leasing, see 64.91- security dealing on behalf of others, see 66.12- trade, leasing and rental of real estate property, see division 68- bill collection without debt buying up, see 82.91- grant-giving activities by membership organisations, see 94.99 ', N'64.9 ', 739)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (743, 399223, 2, N'65 ', N'Insurance, reinsurance and pension funding, except compulsory social security ', N'65 ', N'This division includes the underwriting annuities and insurance policies and investing premiums to build up a portfolio of financial assets to be used against future claims. Provision of direct insurance and reinsurance are included. ', N'', N'', N'', N'K ', 730)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (744, 399224, 3, N'65.1 ', N'Insurance ', N'651 ', N'This group includes life insurance with or without a substantial savings element and non-life insurance. ', N'', N'', N'', N'65 ', 743)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (745, 399225, 4, N'65.11 ', N'Life insurance ', N'6511 ', N'This class includes:- underwriting annuities and life insurance policies, disability income insurance policies, and accidental death and dismemberment insurance policies (with or without a substantial savings element) ', N'', N'- Death or funeral insurance ', N'', N'65.1 ', 744)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (746, 399226, 4, N'65.12 ', N'Non-life insurance ', N'6512 ', N'This class includes:- provision of insurance services other than life insurance:  • accident and fire insurance  • health insurance  • travel insurance  • property insurance  • motor, marine, aviation and transport insurance  • pecuniary loss and liability insurance ', N'', N'- Provision of motor vehicle warranty- Health plan management on own account ', N'', N'65.1 ', 744)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (747, 399227, 3, N'65.2 ', N'Reinsurance ', N'652 ', N'', N'', N'', N'', N'65 ', 743)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (748, 399228, 4, N'65.20 ', N'Reinsurance ', N'6520 ', N'This class includes:- activities of assuming all or part of the risk associated with existing insurance policies originally underwritten by other insurance carriers ', N'', N'', N'', N'65.2 ', 747)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (749, 399229, 3, N'65.3 ', N'Pension funding ', N'653 ', N'', N'', N'', N'', N'65 ', 743)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (750, 399230, 4, N'65.30 ', N'Pension funding ', N'6530 ', N'This class includes legal entities (i.e. funds, plans and/or programmes) organised to provide retirement income benefits exclusively for the sponsor''s employees or members. This includes pension plans with defined benefits, as well as individual plans where benefits are simply defined through the member’s contribution.This class includes:- employee benefit plans- pension funds and plans- retirement plans ', N'', N'- Fund management on behalf of the members ', N'This class excludes:- management of pension funds, see 66.30- compulsory social security schemes, see 84.30 ', N'65.3 ', 749)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (751, 399231, 2, N'66 ', N'Activities auxiliary to financial services and insurance activities ', N'66 ', N'This division includes the provision of services involved in or closely related to financial service activities, but not themselves providing financial services. The primary breakdown of this division is according to the type of financial transaction or funding served. ', N'', N'', N'', N'K ', 730)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (752, 399232, 3, N'66.1 ', N'Activities auxiliary to financial services, except insurance and pension funding ', N'661 ', N'This group includes the furnishing of physical or electronic marketplaces for the purpose of facilitating the buying and selling of stocks, stock options, bonds or commodity contracts. ', N'', N'', N'', N'66 ', 751)
