INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (753, 399233, 4, N'66.11 ', N'Administration of financial markets ', N'6611 ', N'This class includes the operation and supervision of financial markets other than by public authorities, such as:- commodity contracts exchanges- futures commodity contracts exchanges- securities exchanges- stock exchanges- stock or commodity options exchanges ', N'', N'', N'', N'66.1 ', 752)
