INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (754, 399234, 4, N'66.12 ', N'Security and commodity contracts brokerage ', N'6612 ', N'This class includes:- dealing in financial markets on behalf of others (e.g. stock broking) and related activities- securities brokerage- commodity contracts brokerage - activities of bureaux de change etc. ', N'', N'- Buying and selling wind farms as investment projects- Currency trading on the Internet ', N'This class excludes:- dealing in markets on own account, see 64.99- portfolio management, on a fee or contract basis, see 66.30 ', N'66.1 ', 752)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (755, 399235, 4, N'66.19 ', N'Other activities auxiliary to financial services, except insurance and pension funding ', N'6619 ', N'This class includes activities auxiliary to financial service activities not elsewhere classified, such as:- financial transaction processing and settlement activities, including for credit card transactions- investment advisory services - activities of mortgage advisers and brokers ', N'This class also includes:- trustee, fiduciary and custody services on a fee or contract basis ', N'- Tax refund offices- Cash-pooling activities- Investment crowd funding for start-ups ', N'This class excludes:- activities of insurance agents and brokers, see 66.22- management of funds, see 66.30 ', N'66.1 ', 752)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (756, 399236, 3, N'66.2 ', N'Activities auxiliary to insurance and pension funding ', N'662 ', N'This group includes acting as agents (i.e. brokers) in selling annuities and insurance policies or provide other employee benefits and insurance and pension related services such as claims adjustment and third party administration. ', N'', N'', N'', N'66 ', 751)
