INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (757, 399237, 4, N'66.21 ', N'Risk and damage evaluation ', N'6621 ', N'This class includes the provision of administration services of insurance, such as assessing and settling insurance claims, such as:- assessing insurance claims  • claims adjusting  • risk assessing  • risk and damage evaluation  • average and loss adjusting- settling insurance claims ', N'', N'- Insurance claims settlement ', N'This class excludes:- appraisal of real estate, see 68.31- appraisal for other purposes, see 74.90- investigation activities, see 80.30 ', N'66.2 ', 756)
