INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (759, 399239, 4, N'66.29 ', N'Other activities auxiliary to insurance and pension funding ', N'6629 ', N'This class includes:- activities involved in or closely related to insurance and pension funding (except financial intermediation, claims adjusting and activities of insurance agents):  • salvage administration  • actuarial services ', N'', N'- Administration of Insurance Guarantee Fund (such as green card agreement) ', N'This class excludes:- marine salvage activities, see 52.22 ', N'66.2 ', 756)
