INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (762, 399242, 1, N'L ', N'REAL ESTATE ACTIVITIES ', N'L ', N'This section includes acting as lessors, agents and/or brokers in one or more of the following: selling or buying real estate, rental real estate, providing other real estate services such as appraising real estate or acting as real estate escrow agents. Activities in this section may be carried out on own or leased property and may be done on a fee or contract basis. This section includes real estate property managers. ', N'Also included is the building of structures, combined with maintaining ownership or leasing of such structures. ', N'', N'', N'', NULL)
