INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (765, 399245, 4, N'68.10 ', N'Buying and selling of own real estate ', N'6810 ', N'This class includes:- buying and selling of self-owned real estate:  • apartment buildings and dwellings  • non-residential buildings, including exhibition halls, self-storage facilities, malls and shopping centres  • land ', N'This class also includes- subdividing real estate into lots, without land improvement ', N'', N'This class excludes:- development of building projects for sale, see 41.10- subdividing and improving of land, see 42.99 ', N'68.1 ', 764)
