INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (767, 399247, 4, N'68.20 ', N'Rental and operating of own or leased real estate ', N'6810 ', N'This class includes:- rental and operating of self-owned or leased real estate:  • apartment buildings and dwellings  • non-residential buildings, including exhibition halls, self-storage facilities  • land- provision of homes and furnished or unfurnished flats or apartments for more permanent use, typically on a monthly or annual basis ', N'This class also includes:- development of building projects for own operation- operation of residential mobile home sites ', N'- Letting of roofs for solar power installations- Rental of photovoltaic power plants ', N'This class excludes:- operation of hotels, suite hotels, holiday homes, rooming houses, campgrounds, trailer parks and other non-residential or short-stay accommodation places, see division 55 ', N'68.2 ', 766)
