INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (769, 399249, 4, N'68.31 ', N'Real estate agencies ', N'6820 ', N'This class includes the provision of real estate activities by real estate agencies:- intermediation in buying, selling and rental of real estate on a fee or contract basis- advisory activities and appraisal services in connection to buying, selling and rental of real estate, on a fee or contract basis- real estate escrow agents activities ', N'', N'', N'This class excludes:- legal activities, see 69.10 ', N'68.3 ', 768)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (770, 399250, 4, N'68.32 ', N'Management of real estate on a fee or contract basis ', N'6820 ', N'', N'This class also includes:- rent-collecting agencies ', N'- Management by owners of co-owned dwelling facilities ', N'This class excludes:- legal activities, see 69.10- facilities support services (combination of services such as general interior cleaning, maintenance and making minor repairs, trash disposal, guard and security), see 81.10- management of facilities, such as military bases, prisons and other facilities (except computer facilities management), see 81.10 ', N'68.3 ', 768)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (771, 399251, 1, N'M ', N'PROFESSIONAL, SCIENTIFIC AND TECHNICAL ACTIVITIES ', N'M ', N'This section includes specialised professional, scientific and technical activities. These activities require a high degree of training, and make specialised knowledge and skills available to users. ', N'', N'', N'', N'', NULL)
