INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (774, 399254, 4, N'69.10 ', N'Legal activities ', N'6910 ', N'This class includes:- legal representation of one party''s interest against another party, whether or not before courts or other judicial bodies by, or under supervision of, persons who are members of the bar:  • advice and representation in civil cases  • advice and representation in criminal cases  • advice and representation in connection with labour disputes- general counselling and advising, preparation of legal documents:  • articles of incorporation, partnership agreements or similar documents in connection with company formation  • patents and copyrights  • preparation of deeds, wills, trusts etc.- other activities of notaries public, civil law notaries, bailiffs, arbitrators, examiners and referees ', N'', N'', N'This class excludes:- law court activities, see 84.23 ', N'69.1 ', 773)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (775, 399255, 3, N'69.2 ', N'Accounting, bookkeeping and auditing activities; tax consultancy ', N'692 ', N'', N'', N'', N'', N'69 ', 772)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (776, 399256, 4, N'69.20 ', N'Accounting, bookkeeping and auditing activities; tax consultancy ', N'6920 ', N'This class includes:- recording of commercial transactions from businesses or others- preparation or auditing of financial accounts- examination of accounts and certification of their accuracy- preparation of personal and business income tax returns- advisory activities and representation on behalf of clients before tax authorities ', N'', N'- Duty and tax tariff calculation service activities ', N'This class excludes:- data-processing and tabulation activities, see 63.11- management consultancy on accounting systems, budgetary control procedures, see 70.22- bill collection, see 82.91 ', N'69.2 ', 775)
