INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (777, 399257, 2, N'70 ', N'Activities of head offices; management consultancy activities ', N'70 ', N'This division includes the provision of advice and assistance to businesses and other organisations on management issues, such as strategic and organisational planning; financial planning and budgeting; marketing objectives and policies; human resource policies, practices, and planning; production scheduling; and control planning. ', N'It also includes the overseeing and managing of other units of the same company or enterprise, i.e. the activities of head offices. ', N'', N'', N'M ', 771)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (778, 399258, 3, N'70.1 ', N'Activities of head offices ', N'701 ', N'', N'', N'', N'', N'70 ', 777)
