INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (779, 399259, 4, N'70.10 ', N'Activities of head offices ', N'7010 ', N'This class includes the overseeing and managing of other units of the company or enterprise; undertaking the strategic or organisational planning and decision making role of the company or enterprise; exercising operational control and managing the day-to-day operations of their related units.This class includes activities of:- head offices- centralised administrative offices- corporate offices- district and regional offices- subsidiary management offices ', N'', N'', N'This class excludes:- activities of holding companies, not engaged in managing, see 64.20 ', N'70.1 ', 778)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (780, 399260, 3, N'70.2 ', N'Management consultancy activities ', N'702 ', N'', N'', N'', N'', N'70 ', 777)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (781, 399261, 4, N'70.21 ', N'Public relations and communication activities ', N'7020 ', N'This class includes the provision of advice, guidance and operational assistance, including lobbying activities, to businesses and other organisations on public relations and communication. ', N'', N'', N'This class excludes:- advertising agencies and media representation services, see 73.1- market research and public opinion polling, see 73.20 ', N'70.2 ', 780)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (782, 399262, 4, N'70.22 ', N'Business and other management consultancy activities ', N'7020 ', N'This class includes the provision of advice, guidance and operational assistance to businesses and other organisations on management issues, such as corporate strategic and organisational planning, business process reengineering, change management, cost reduction and other financial issues; marketing objectives and policies; human resource policies, practices and planning; compensation and retirement strategies; production scheduling and control planning.This provision of business services may include advice, guidance or operational assistance to businesses and the public service regarding:- design of accounting methods or procedures, cost accounting programmes, budgetary control procedures- advice and help to businesses and public services in planning, organisation, efficiency and control, management information etc. ', N'', N'- Provision of business and professionals guidance- Filling out the papers and attaching the supporting documents required in relation to a public procurement procedure- Business establishing and start-up service activities ', N'This class excludes:- design of computer software for accounting systems, see 62.01- legal advice and representation, see 69.10- accounting, bookkeeping and auditing activities, tax consulting, see 69.20- architectural and engineering advisory activities, see 71.11, 71.12- environmental, agronomy, security and similar consulting activities, see 74.90- executive placement or search consulting activities, see 78.10- educational consulting activities, see 85.60 ', N'70.2 ', 780)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (783, 399263, 2, N'71 ', N'Architectural and engineering activities; technical testing and analysis ', N'71 ', N'This division includes the provision of architectural services, engineering services, drafting services, building inspection services and surveying and mapping services. ', N'It also includes the performance of physical, chemical, and other analytical testing services. ', N'', N'', N'M ', 771)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (784, 399264, 3, N'71.1 ', N'Architectural and engineering activities and related technical consultancy ', N'711 ', N'This group includes the provision of architectural services, engineering services, drafting services, building inspection services and surveying and mapping services and the like. ', N'', N'', N'', N'71 ', 783)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (785, 399265, 4, N'71.11 ', N'Architectural activities ', N'7110 ', N'This class includes:- architectural consulting activities:  • building design and drafting  • town and city planning and landscape architecture ', N'', N'', N'This class excludes:- activities of computer consultants, see 62.02, 62.09- interior decorating, see 74.10 ', N'71.1 ', 784)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (786, 399266, 4, N'71.12 ', N'Engineering activities and related technical consultancy ', N'7110 ', N'This class includes:- engineering design (i.e. applying physical laws and principles of engineering in the design of machines, materials, instruments, structures, processes and systems) and consulting activities for:  • machinery, industrial processes and industrial plant  • projects involving civil engineering, hydraulic engineering, traffic engineering  • water management projects  • projects elaboration and realisation relative to electrical and electronic engineering, mining engineering, chemical engineering, mechanical, industrial and systems engineering, safety engineering- elaboration of projects using air conditioning, refrigeration, sanitary and pollution control engineering, acoustical engineering etc.- geophysical, geologic and seismic surveying- geodetic surveying activities:  • land and boundary surveying activities  • hydrologic surveying activities  • subsurface surveying activities  • cartographic and spatial information activities ', N'', N'- Updating of navigation maps- Go-kart track design activities ', N'This class excludes:- test drilling in connection with mining operations, see 09.10, 09.90- development or publishing of associated software, see 58.29, 62.01- activities of computer consultants, see 62.02, 62.09- technical testing, see 71.20- research and development activities related to engineering, see 72.19- industrial design, see 74.10- aerial photography, see 74.20 ', N'71.1 ', 784)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (787, 399267, 3, N'71.2 ', N'Technical testing and analysis ', N'712 ', N'', N'', N'', N'', N'71 ', 783)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (788, 399268, 4, N'71.20 ', N'Technical testing and analysis ', N'7120 ', N'This class includes the performance of physical, chemical and other analytical testing of all types of materials and products, such as:  • acoustics and vibration testing  • testing of composition and purity of minerals etc.  • testing activities in the field of food hygiene, including veterinary testing and control in relation to food production  • testing of physical characteristics and performance of materials, such as strength, thickness, durability, radioactivity etc.  • qualification and reliability testing  • performance testing of complete machinery: motors, automobiles, electronic equipment etc.  • radiographic testing of welds and joints  • failure analysis  • testing and measuring of environmental indicators: air and water pollution etc.- certification of products, including consumer goods, motor vehicles, aircraft, pressurised containers, nuclear plants etc.- periodic road-safety testing of motor vehicles- testing with use of models or mock-ups (e.g. of aircraft, ships, dams etc.)- operation of police laboratories ', N'', N'- Archaeobotanical analyses- Inspection of oilfield pipes- Product origin and quality assessment activities- Think tanks (policy institutes) conducting research and experimental development on social sciences and humanities- Excavation, digging on archaeological sites and research ', N'This class excludes:- testing of animal specimens, see 75.00- diagnostic imaging, testing and analysis of medical and dental specimens, see 86 ', N'71.2 ', 787)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (789, 399269, 2, N'72 ', N'Scientific research and development ', N'72 ', N'This division includes the activities of three types of research and development: 1) basic research: experimental or theoretical work undertaken primarily to acquire new knowledge of the underlying foundations of phenomena and observable facts, without particular application or use in view, 2) applied research: original investigation undertaken in order to acquire new knowledge, directed primarily towards a specific practical aim or objective and 3) experimental development: systematic work, drawing on existing knowledge gained from research and/or practical experience, directed to producing new materials, products and devices, to installing new processes, systems and services, and to improving substantially those already produced or installed.Research and experimental development activities in this division are subdivided into two categories: natural sciences and engineering; social sciences and the humanities. ', N'', N'', N'This division excludes:- market research, see 73.20 ', N'M ', 771)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (790, 399270, 3, N'72.1 ', N'Research and experimental development on natural sciences and engineering ', N'721 ', N'This group comprises basic research, applied research, experimental development in natural sciences and engineering. ', N'', N'', N'', N'72 ', 789)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (791, 399271, 4, N'72.11 ', N'Research and experimental development on biotechnology ', N'7210 ', N'This class includes research and experimental development on biotechnology:- DNA/RNA: genomics, pharmacogenomics, gene probes, genetic engineering, DNA/RNA sequencing/synthesis/amplification, gene expression profiling, and use of antisense technology- proteins and other molecules: sequencing/synthesis/engineering of proteins and peptides (including large molecule hormones); improved delivery methods for large molecule drugs; proteomics, protein isolation and purification, signalling, identification of cell receptors- cell and tissue culture and engineering: cell/tissue culture, tissue engineering (including tissue scaffolds and biomedical engineering), cellular fusion, vaccine/immune stimulants, embryo manipulation- process biotechnology techniques: fermentation using bioreactors, bioprocessing, bioleaching, biopulping, biobleaching, biodesulphurisation, bioremediation, biofiltration and phytoremediation- gene and RNA vectors: gene therapy, viral vectors- bioinformatics: construction of databases on genomes, protein sequences; modelling complex biological processes, including systems biology- nanobiotechnology: applies the tools and processes of nano/microfabrication to build devices for studying biosystems and applications in drug delivery, diagnostics etc. ', N'', N'', N'', N'72.1 ', 790)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (792, 399272, 4, N'72.19 ', N'Other research and experimental development on natural sciences and engineering ', N'7210 ', N'This class includes:- research and experimental development on natural science and engineering other than biotechnological research and experimental development:  • research and development on natural sciences  • research and development on engineering and technology  • research and development on medical sciences  • research and development on agricultural sciences  • interdisciplinary research and development, predominantly on natural sciences and engineering ', N'', N'', N'', N'72.1 ', 790)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (793, 399273, 3, N'72.2 ', N'Research and experimental development on social sciences and humanities ', N'722 ', N'', N'', N'', N'', N'72 ', 789)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (794, 399274, 4, N'72.20 ', N'Research and experimental development on social sciences and humanities ', N'7220 ', N'This class includes:- research and development on social sciences- research and development on humanities- interdisciplinary research and development, predominantly on social sciences and humanities ', N'', N'Excavation, digging on archaeological sites and research ', N'This class excludes:- market research, see 73.20 ', N'72.2 ', 793)
GO
INSERT [dbo].[NACE] ([ID], [Order], [Level], [Code], [Description], [ISICRef], [Includes], [IncludesAlso], [Rulings], [Excludes], [ParentCode], [ParentId]) VALUES (795, 399275, 2, N'73 ', N'Advertising and market research ', N'73 ', N'This division includes the creation of advertising campaigns and placement of such advertising in periodicals, newspapers, radio and television, or other media as well as the design of display structures and sites. ', N'', N'', N'', N'M ', 771)
